'\r\n						. $this->_format_styleclass($this->field_styles[$element_type]) .$element_attributes .'' '';\r\n				$e .= '' />'';			\r\n				break;\r\n			case ''captchaimage'':\r\n				$this->init_captcha_object();\r\n				$e = $this->captcha_object->getCaptchaHtml($element_name, \r\n								$this->captchavals[''length''], \r\n								$this->_format_styleclass($this->field_styles[$element_type]) .$element_attributes, \r\n								$this->captchavals[''attributes''], \r\n								$this->captchavals[''styleset''], \r\n								$this->captchavals[''width''], \r\n								$this->captchavals[''height'']);\r\n				break;\r\n			case ''label''://label_as_formfield, placeholder\r\n				$e = ''<span ''.$this->_format_styleclass($this->field_styles[''label_as_formfield'']).'' >''.$element_value.''</span>'';\r\n				break;\r\n			case ''locumtenency'':\r\n				$e = ''<span ''.$this->_format_styleclass($this->field_styles[''placeholder'']).'' >''. $this->separator_tpl .''</span>'';\r\n				break;			\r\n		}\r\n		\r\n		return $e;	\r\n	}\r\n	\r\n	function init_captcha_object() \r\n	{\r\n		global $cfg_client;\r\n		\r\n		if (is_object($this->captcha_object)) \r\n		{\r\n			return true;\r\n		} \r\n		else\r\n		{\r\n			$this->captcha_object =& sf_factoryGetObject(''GUI'', ''CaptchaImage'');\r\n			$this->captcha_object->setCaptchaTemplate($this->captchavals[''template'']);\r\n			return true;\r\n		}\r\n	}\r\n	\r\n	function get_value($element_type, $element_name, $element_default, $element_selected, $skip_default = false)\r\n	{\r\n		//echo "$element_type, $element_name, $element_default, $element_selected, $skip_default <br>";\r\n		if(isset($_REQUEST[$element_name]))\r\n		{\r\n			$v = is_array($_REQUEST[$element_name]) ? implode($this->mutiple_value_separator, $_REQUEST[$element_name]): $_REQUEST[$element_name];\r\n			return $this->_strip_slashes($v);\r\n		} \r\n		else if (($element_type == ''checkbox'' || $element_type == ''checkboxSelected'' || $element_type == ''radio'') \r\n					&& ! $_REQUEST[$element_name] && $this->is_submitted()) \r\n		{\r\n			return false;\r\n		}\r\n		else if ($element_selected != '''') \r\n		{\r\n			return $element_selected;\r\n		} \r\n		else if ($skip_default) \r\n		{\r\n			return false;\r\n		}\r\n	\r\n		return $element_default;\r\n	}\r\n	\r\n	\r\n	function validate($element_validate , $element_value)\r\n	{\r\n		if(! $this->is_submitted())\r\n		{\r\n			return true;\r\n		}\r\n		\r\n		//validate captcha\r\n		if ($element_validate == ''imagecaptcha'') \r\n		{\r\n			//do nothing\r\n		} \r\n		//validate regex\r\n		else if(array_key_exists($element_validate, $this->rules))\r\n		{\r\n			if(! preg_match($this->rules[$element_validate], $element_value))\r\n			{\r\n				$this->is_rule_violation = true;\r\n				return false;				\r\n			}\r\n		}\r\n		\r\n		return true;\r\n	}\r\n	\r\n	function validate_upload($element_name, $element_label, $validate_rule, $stack_key)\r\n	{\r\n		$upload_dir = $this->upload_dir;\r\n		$upload_allowed_extension = $this->upload_allowed_extensions;\r\n		$upload_allowed_filesize = $this->upload_allowed_filesize;\r\n	\r\n		$size = $_FILES[$element_name][''size''];\r\n		$name = $_FILES[$element_name][''name''];\r\n		$type = $_FILES[$element_name][''type''];\r\n		$tmp_name = $_FILES[$element_name][''tmp_name''];\r\n		$err = $_FILES[$element_name][''error''];\r\n		$parts = pathinfo(''sicheristsicher/''.$name);\r\n		$ext =  strtolower ($parts[''extension'']);\r\n		$allowed_ext =  strtolower (str_replace('' '', '''',$upload_allowed_extension));\r\n\r\n		$this->uploads[$element_name][''label''] = $element_label;		\r\n		$this->uploads[$element_name][''file''] = '''';\r\n		$this->uploads[$element_name][''error''] = '''';\r\n		$this->uploads[$element_name][''int_error''] = '''';\r\n		\r\n		\r\n		//no file found/ uploaded\r\n		if($tmp_name =='''')\r\n		{\r\n			//check if upload is required\r\n			if($validate_rule == ''required'')\r\n			{\r\n				$this->is_rule_violation = true;\r\n				return false;	\r\n			} \r\n			else\r\n			{\r\n				return true;	\r\n			}\r\n		}\r\n\r\n		$upload_error = '''';\r\n		//check php errors\r\n		switch($err)\r\n		{\r\n			case 1:\r\n				$upload_error = $this->upload_errors[''deb_filesize_php_ini''];\r\n				break;\r\n			case 2:\r\n				$upload_error = $this->upload_errors[''deb_filesize_browser''];\r\n				break;\r\n			case 3:\r\n				$upload_error = $this->upload_errors[''deb_filesize_incremental''];\r\n				break;\r\n			case 4:\r\n				$upload_error = $this->upload_errors[''deb_no_file''];\r\n				break;								\r\n		}\r\n			\r\n		if($err > 0)\r\n		{\r\n			$this->uploads[$element_name][''error''] = $this->upload_errors[''usr_internal_error''];\r\n			$this->uploads[$element_name][''int_error''] = $upload_error;\r\n			$this->is_rule_violation = true;\r\n			return false;\r\n		}\r\n		\r\n		//check extensions\r\n		if(strlen($allowed_ext) > 0)\r\n		{\r\n			$ax = explode('','', $allowed_ext);\r\n			if(! in_array($ext, $ax))\r\n			{\r\n				$this->uploads[$element_name][''error''] = $this->upload_errors[''usr_allowed_ext''];\r\n				$this->is_rule_violation = true;\r\n				return false;\r\n			}\r\n		}\r\n		\r\n		//checkfilesize\r\n		if($upload_allowed_filesize > 0)\r\n		{\r\n			$upload_allowed_filesize *= 1024;\r\n			if($size >$upload_allowed_filesize){\r\n				$this->uploads[$element_name][''error''] = $this->upload_errors[''usr_allowed_size''];\r\n				$this->is_rule_violation = true;\r\n				return false;	\r\n			}\r\n		}\r\n		\r\n		//move file\r\n		if (! move_uploaded_file($_FILES[$element_name][''tmp_name''], \r\n				$upload_dir . $name))\r\n		{\r\n			\r\n			$this->uploads[$element_name][''error''] = $this->upload_errors[''usr_internal_error''];\r\n			$this->uploads[$element_name][''int_error''] = $this->upload_errors[''deb_filecopy_fails''];\r\n			$this->is_rule_violation = true;\r\n			return false;		\r\n		}\r\n		\r\n		$this->uploads[$element_name][''file''] = $upload_dir . $name;\r\n		$this->uploads[$element_name][''name''] = $name;\r\n		$this->uploads[$element_name][''type''] = $type;\r\n		$this->uploads[$element_name][''size''] = $size;\r\n		$this->uploads[$element_name][''ext'']  = $ext;\r\n		$this->stack[$stack_key][''advanced''] = $this->uploads[$element_name];\r\n		return true;	\r\n	}\r\n	\r\n	\r\n	/*\r\n	 * -----------------------------------------------------------\r\n	 * "FORM TO FINAL HTML" AND SOME MISC METHODS\r\n	 * -----------------------------------------------------------\r\n	 */\r\n	function get_form(){\r\n		return $this->_get_form();\r\n	}\r\n	\r\n	function get_form_plain(){\r\n		return $this->_get_form(true);\r\n	}\r\n	\r\n	function _get_form($is_plain = false){\r\n		global $con_side, $con_tree, $cfg_client, $idcatside;\r\n		\r\n		$frm_vals[''formstart''] = $this->get_form_head()."\\n";\r\n		$frm_vals[''formend''] = $this->get_form_footer()."\\n";\r\n		$frm_vals[''submit''] = $this->get_submitbutton();\r\n		\r\n		$arr[''email''] = '''';\r\n		if ( $this->email_receiptor_mode == ''get'' && $this->email_receiptor_get_mode_idcatside > 0) {\r\n			$arr = $this->get_receiptor_data_from_content_as_array();\r\n			$frm_vals[''textareafromdynamic''] = nl2br($arr[''textareafromdynamic'']);\r\n			$frm_vals[''textfromdynamic''] = $arr[''textfromdynamic''];\r\n			\r\n			$idcatside_from = $this->email_receiptor_get_mode_idcatside;\r\n			$frm_vals[''page_name''] = $con_side[$idcatside_from][''name''];\r\n			$frm_vals[''page_url''] = $cfg_client[''htmlpath''] . str_replace(''&amp;'', ''&'', $con_side[$idcatside_from][''link'']);\r\n			$frm_vals[''cat_name''] = $con_tree[ $con_side[$idcatside_from][''idcat''] ][''name''];\r\n			$frm_vals[''cat_url''] =  $cfg_client[''htmlpath''] . str_replace(''&amp;'', ''&'', $con_tree[ $con_side[$idcatside_from][''idcat''] ][''link'']);		\r\n		} else {\r\n			$frm_vals[''textareafromdynamic''] = nl2br($this->fallback_textareafromdynamic);\r\n			$frm_vals[''textfromdynamic''] = $this->fallback_textfromdynamic;	\r\n			$frm_vals[''page_name''] = $con_side[$idcatside][''name''];\r\n			$frm_vals[''page_url''] = $cfg_client[''htmlpath''] .  str_replace(''&amp;'', ''&'', $con_side[$idcatside][''link'']);\r\n			$frm_vals[''cat_name''] = $con_tree[ $con_side[$idcatside][''idcat''] ][''name''];\r\n			$frm_vals[''cat_url''] =  $cfg_client[''htmlpath''] .  str_replace(''&amp;'', ''&'', $con_tree[ $con_side[$idcatside][''idcat''] ][''link'']);						\r\n		}\r\n		\r\n		if($is_plain)\r\n			$frm_vals[''formelementblock''] = $this->parsed_plain;\r\n		else\r\n			$frm_vals[''formelementblock''] = $this->parsed;\r\n		$frm_vals[''usermessage''] = $this->get_usermessage();\r\n		if ($frm_vals[''usermessage''] != '''')\r\n			$frm_vals[''messageblock''] = str_replace(''{usermessage}'',$this->get_usermessage(), $this->message_block_tpl);\r\n		else\r\n			$frm_vals[''messageblock''] = '''';\r\n		\r\n		$tpl = $this->combination_tpl;\r\n		foreach($frm_vals AS $k=>$v){\r\n			$tpl = str_replace(''{''.$k.''}'', $v, $tpl);\r\n		}\r\n		\r\n		$deb = ($this->debug) ? $this->debugmessage: '''';\r\n		$tpl = str_replace(''{debugmessages}'', $deb, $tpl);\r\n		return $tpl;		\r\n	}\r\n	\r\n	function get_form_head(){\r\n		$a = str_replace(''{url}'', $this->form_target_url, $this->tpl_formstarttag);\r\n		if ($_REQUEST[''formSubmitted''] == ''true'' AND $this->is_validated() ){ $a = str_replace(''{transmitted}'', "transmitted", $a);}\r\n		return $a;\r\n	}\r\n	\r\n	function get_form_footer(){\r\n		return $this->hiddenvalues . $this->tpl_formendtag;\r\n	}\r\n	\r\n	function get_submitbutton(){\r\n		return ''<input type="submit" name="fmsubmit" ''. \r\n					$this->_format_styleclass($this->field_styles[''submitbutton'']) \r\n					.'' value="''.$this->submitbutton_text.''" />'';\r\n	}\r\n	\r\n	function get_usermessage(){\r\n		\r\n		$m = '''';\r\n		if($this->is_submitted()){\r\n			if($this->is_validated())\r\n				$m = $this->_get_styled_message($this->message_success, $this->field_styles[''submit'']);\r\n			else\r\n				$m = $this->_get_styled_message($this->message_error .'' ''. $this->get_invalid_fields(), \r\n								$this->field_styles[''submit_error'']);\r\n		}\r\n		\r\n		return $m;\r\n	}\r\n	\r\n	\r\n	function get_invalid_fields(){\r\n		return implode('', '', $this->invalid_fields);	\r\n	}\r\n	\r\n	function get_stack() {\r\n		return $this->stack;\r\n	}\r\n	\r\n	function is_validated(){\r\n		return  ( !$this->is_rule_violation);		\r\n	}\r\n	\r\n	function is_submitted(){\r\n		return ($_REQUEST[''formSubmitted''] == ''true'');\r\n	}\r\n	\r\n	\r\n	/*\r\n	 * -----------------------------------------------------------\r\n	 * MAIL METHODS\r\n	 * -----------------------------------------------------------\r\n	 */	\r\n	function send_mail(){\r\n		global $con_side, $con_tree, $cfg_client, $idcatside;	\r\n		\r\n		$tpl_email = $this->tpl_email;\r\n	\r\n		$to = '''';\r\n		if ($this->email_receiptor_mode == ''get'') {\r\n			$arr = $this->get_receiptor_data_from_content_as_array();\r\n			$to = trim($arr[''email'']);\r\n			\r\n			$idcatside_from = $this->email_receiptor_get_mode_idcatside;\r\n			$page_name = $con_side[$idcatside_from][''name''];\r\n			$page_url = $cfg_client[''htmlpath''] . str_replace(''&amp;'', ''&'', $con_side[$idcatside_from][''link'']);\r\n			$cat_name = $con_tree[ $con_side[$idcatside_from][''idcat''] ][''name''];\r\n			$cat_url =  $cfg_client[''htmlpath''] . str_replace(''&amp;'', ''&'', $con_tree[ $con_side[$idcatside_from][''idcat''] ][''link'']);\r\n		}\r\n		\r\n		if ($this->email_receiptor_mode == ''textfield'' || $to == '''') {\r\n			$to = $this->email_receiptor_textfield_mode_mailadress;\r\n			\r\n			if ($this->email_receiptor_mode == ''textfield'') {\r\n				$page_name = $con_side[$idcatside][''name''];\r\n				$page_url = $cfg_client[''htmlpath''] .  str_replace(''&amp;'', ''&'', $con_side[$idcatside][''link'']);\r\n				$cat_name = $con_tree[ $con_side[$idcatside][''idcat''] ][''name''];\r\n				$cat_url =  $cfg_client[''htmlpath''] .  str_replace(''&amp;'', ''&'', $con_tree[ $con_side[$idcatside][''idcat''] ][''link'']);\r\n				\r\n				$arr[''textfromdynamic''] = $this->fallback_textfromdynamic;\r\n				$arr[''textareafromdynamic''] = $this->fallback_textareafromdynamic;\r\n			}\r\n		}\r\n		\r\n		\r\n		//no emailadress found - break up\r\n		if ($to =='''' && $this->send_mail) {\r\n			return;\r\n		}\r\n			\r\n		$sender = $this->email_sender;\r\n		$subject = $this->generate_email_template($this->email_subject);\r\n		\r\n		\r\n\r\n		$mail =& $GLOBALS[''sf_factory'']->getObject(''UTILS'', ''Mail'');\r\n		$mail->setCharset($this->charset);\r\n		$mail->setFrom($sender);\r\n		$mail->setSubject($subject);\r\n		\r\n		$arr[''to''] = explode('','', $to);\r\n		$arr[''cc''] = explode('','', $this->email_receiptor_cc);\r\n		$arr[''bcc''] = explode('','', $this->email_receiptor_bcc);\r\n		foreach ($arr AS $k=>$v) {		\r\n			if (is_array($v)) {\r\n				foreach ($v AS $adr) {\r\n					$adr = trim($adr);\r\n					if (substr($adr, 0, 1) == ''+'') {\r\n						$mail->addAdressByUsername(substr($adr, 1), $k);\r\n					} else if (substr($adr, 0, 1) == ''#'') {\r\n						$mail->addAdressByGroupname(substr($adr, 1), $k);\r\n					} else {\r\n						$mail->addAdress($adr, $k);\r\n					}\r\n				}\r\n			}\r\n		}\r\n		\r\n		//add attachements\r\n		if (is_array($this->uploads)){\r\n			foreach($this->uploads AS $k=>$v){\r\n				if($v[''file''] != ''''){\r\n					$mail->addAttachment($v[''file''], $v[''type''], $v[''name'']);\r\n				}\r\n			}\r\n		}\r\n		\r\n		//give possibility to manipulate the stack\r\n		////////////////////////////////////////////////////////$this->addon->onSuccess($this->stack);\r\n		\r\n		//generate email template\r\n		$tpl_email = $this->generate_email_template($tpl_email, $to, $arr[''textfromdynamic''], $arr[''textareafromdynamic''],\r\n										$page_name, $page_url, $cat_name, $cat_url);\r\n		//echo $tpl_email;exit;\r\n		$mail->setTxtBody($tpl_email);\r\n		\r\n		if ($this->debug){\r\n			$this->debugmessage .= "Mail to: $to || Mail from:  $sender || Mail subject: $subject <br>";\r\n			echo $this->debugmessage .= ''Mailtext:<br>-------------<br>''.nl2br($tpl_email).''<br>-------------<br>'';\r\n		} \r\n		\r\n		if($this->send_mail) {\r\n			$result = $mail->process();//$mail->send(array($to), ''mail''); \r\n		}\r\n		\r\n		//send second email\r\n		if ($this->confirm_mail_active) {\r\n			$mail->adresses = array(''to'' => array(),\r\n    							''cc'' => array(),\r\n    							''bcc'' => array());\r\n			$tpl_email2 = $this->generate_email_template($this->confirm_mail_tpl, $to, $arr[''textfromdynamic''], $arr[''textareafromdynamic''],\r\n										$page_name, $page_url, $cat_name, $cat_url);    							\r\n    		\r\n    		\r\n    		//get sender\r\n    		if(is_numeric(trim($this->second_email_sender))) {\r\n    			foreach ($this->stack AS $k=>$v)\r\n    			{\r\n    				if ($v[''elementnumber''] == (int) $this->second_email_sender) {\r\n    					$sender = $v[''value''];\r\n    				}\r\n    			}\r\n    		} else {\r\n    			$sender = $this->second_email_sender;\r\n    		}\r\n    		\r\n    		//get recipient\r\n    		if(is_numeric(trim($this->second_email_recipient))) {\r\n    			foreach ($this->stack AS $k=>$v)\r\n    			{\r\n    				if ($v[''elementnumber''] == (int) $this->second_email_recipient) {\r\n    					$recipient = $v[''value''];\r\n    				}\r\n    			}\r\n    		} else {\r\n    			$recipient = $this->second_email_recipient;\r\n    		}\r\n    		\r\n    		\r\n    		$rec_array = explode($this->mutiple_value_separator, $recipient);\r\n    		\r\n    		\r\n    		$this->confirm_mail_subject = $this->generate_email_template($this->confirm_mail_subject);\r\n    				\r\n    		$mail->setFrom($sender);\r\n			$mail->setSubject($this->confirm_mail_subject);\r\n			\r\n			foreach ($rec_array AS $v)\r\n			{\r\n				$v2 = explode($this->key_value_separator, $v);\r\n				$mail->addAdress($v2[''0''], ''to'');\r\n			}\r\n\r\n			$mail->setTxtBody($tpl_email2);\r\n			$result = $mail->process();\r\n		}\r\n		\r\n		if ($this->logging_active) {\r\n			$mailstatus = ($result) ? ''OK'' : ''Error - Mailfunction does not work'';\r\n			$logmessage = sprintf( "%s: Send Mail: %s%s\\n\\n",\r\n	            date( ''Y-m-d (D) H:i:s'' ), \r\n				"IP: ".$_SERVER[''REMOTE_ADDR''] ." Mail to: $to || Mail from:  $sender || Mail subject: $subject \\nStatus: $mailstatus \\n-------------------------------\\n", \r\n				"Mail Conent1: $tpl_email \\n-------------------------------\\n"\r\n				."Mail Conent2: $tpl_email2 \\n-------------------------------\\n");\r\n			$this->_log($logmessage);\r\n		}\r\n		\r\n		\r\n	}\r\n	\r\n	function generate_email_template($tpl_email, $to ='''', $textfromdynamic ='''', $textareafromdynamic ='''',\r\n										$page_name ='''', $page_url ='''', $cat_name ='''', $cat_url ='''') {\r\n		\r\n		// convert all linebreaks to \\r\\n linebreaks\r\n		$tpl_email = preg_replace("#(\\r\\n)|(\\r)#m", "\\n", $tpl_email);\r\n		//$tpl_email = preg_replace("#(\\n)#m", "\\r\\n", $tpl_email);\r\n		\r\n		\r\n		// fill emailtemplate\r\n		$tpl_email = str_replace(array(''{email_receiptor}'', ''{textfromdynamic}'', ''{textareafromdynamic}'',\r\n										''{page_name}'', ''{page_url}'', ''{cat_name}'', ''{cat_url}'') , \r\n								array($to, $textfromdynamic, $textareafromdynamic,\r\n										$page_name, $page_url, $cat_name, $cat_url ),\r\n												 $tpl_email);\r\n		\r\n		$automessage = '''';\r\n		foreach($this->stack AS $k => $v){\r\n			//echo $k.": ";print_r($v);echo "<br><br>";\r\n			switch($v[''type'']){\r\n				case ''label'':\r\n					$tpl_email = str_replace(array(''{elementlabel:''. $v[''elementnumber''] .''}'', ''{elementvalue:''. $v[''elementnumber''] .''}'') , \r\n												array($v[''label''], $v[''value''] ),\r\n												 $tpl_email);\r\n				case ''locumtenency'':\r\n				case ''upload'':\r\n				case ''captchaimage'':\r\n					break;\r\n				case ''optionMutiple'':\r\n					//print_r($v);\r\n					$s = $_REQUEST[$v[''name'']];\r\n					if (count($s) > 0){\r\n						$m_inner = '''';\r\n						foreach($s AS $v2){\r\n							$m_inner .= $this->_strip_slashes($v2). "\\n";\r\n						}\r\n						$automessage .= $v[''label''].":\\n". $m_inner ."\\n\\n";\r\n						$tpl_email = str_replace(array(''{elementlabel:''. $v[''elementnumber''] .''}'', ''{elementvalue:''. $v[''elementnumber''] .''}'') , \r\n												array($v[''label''], $m_inner ),\r\n												 $tpl_email);\r\n					}		\r\n					break;\r\n				default:\r\n					$automessage .= $v[''label''].":\\n". $v[''value'']. "\\n\\n";\r\n					$tpl_email = str_replace(array(''{elementlabel:''. $v[''elementnumber''] .''}'', ''{elementvalue:''. $v[''elementnumber''] .''}'') , \r\n												array($v[''label''], $v[''value''] ),\r\n												 $tpl_email);\r\n			}\r\n		}\r\n		\r\n		if (is_array($this->uploads)){\r\n			foreach($this->uploads AS $k=>$v){\r\n				if($v[''file''] != ''''){\r\n					$automessage .= "ATTACHMENT ''". $v[''label'']."'': ". $v[''name'']. "\\n";\r\n				}\r\n			}\r\n		}\r\n		\r\n		//generate email template\r\n		$tpl_email = str_replace(''{automessage}'', $automessage, $tpl_email);\r\n		\r\n		//replace snippets\r\n		$tpl_email = $this->_snippetReplace($tpl_email);\r\n		\r\n		\r\n		return $tpl_email;\r\n	}\r\n	\r\n	function get_receiptor_data_from_content_as_array(){\r\n		global $db, $cms_db, $lang;\r\n		\r\n		$arr = array(''email'' => '''', ''textfromdynamic'' => '''', ''textareafromdynamic'' => '''', );\r\n		\r\n		if($this->email_receiptor_get_mode_idcatside == '''' \r\n			|| $this->email_receiptor_get_mode_idcontainer =='''')\r\n			return $arr;\r\n		\r\n		$grab_conf[''idcatside''] = $this->email_receiptor_get_mode_idcatside;\r\n		$grab_conf[''idlang''] = $lang;\r\n		$grab_conf[''container''] = $this->email_receiptor_get_mode_idcontainer; \r\n		$grab_conf[''id''] = ''1,2''; //id of cmstag\r\n		$grab_conf[''type''] = ''1,3'';//text + textarea\r\n		\r\n		$sql = "SELECT\r\n					CS.sortindex, CS.idcatside, CS.idcat,\r\n					CONTENT.container, CONTENT.idtype AS type, CONTENT.typenumber AS id, \r\n					CONTENT.number AS repeatid, CONTENT.value\r\n				FROM\r\n					".$cms_db[''content'']." CONTENT,\r\n					".$cms_db[''side_lang'']." SL \r\n					LEFT JOIN ".$cms_db[''cat_side'']."  CS USING (idside)\r\n				WHERE\r\n					CONTENT.idsidelang = SL.idsidelang\r\n					AND CS.idcatside = ".$grab_conf[''idcatside'']."\r\n					AND SL.idlang= ".$grab_conf[''idlang'']." \r\n					AND CONTENT.container IN (".$grab_conf[''container''].")\r\n					AND CONTENT.typenumber IN (".$grab_conf[''id''].")\r\n					AND CONTENT.idtype IN (".$grab_conf[''type''].")";\r\n		$db->query($sql);\r\n		\r\n		while($db->next_record()){\r\n			if ($db->f(''type'') == 1 && $db->f(''id'') == 1){\r\n				if (preg_match($this->rules[''email''], trim($db->f(''value'')))) {\r\n					$arr[''email''] = trim($db->f(''value''));\r\n				}\r\n			} else if ($db->f(''type'') == 1 && $db->f(''id'') == 2){\r\n				$arr[''textfromdynamic''] = $db->f(''value'');\r\n			} else if ($db->f(''type'') == 3 && $db->f(''id'') == 1){\r\n				$arr[''textareafromdynamic''] = $db->f(''value'');\r\n			}\r\n		}	\r\n		\r\n		return $arr;	\r\n	}\r\n	\r\n	\r\n	/*\r\n	 * -----------------------------------------------------------\r\n	 * SET METHODS\r\n	 * -----------------------------------------------------------\r\n	 */\r\n\r\n	/**\r\n	 * Set all used stylesheets for the form\r\n	 * \r\n	 * @args array style_array - possible values are\r\n	 * submit_error, submit (Styles for textmessages), \r\n	 * label, label_error, text, textarea, option, checkbox, submitbutton,\r\n	 * \r\n	 * new:  password, select_mutiple, radio, label_as_formfield, placeholder\r\n	 */\r\n	function set_styles($style_array){\r\n		if(! is_array($style_array) ) return;\r\n		\r\n		foreach($style_array AS $k => $v){\r\n			$this->field_styles[$k] = $v;\r\n		}		\r\n	}\r\n	\r\n	//mainmethod to add form elements\r\n	function set_formelement($element_name, $element_type, $element_default, $element_validate, $element_label,\r\n					$attributes_formelement, $attributes_label, $element_errormessage, $element_tplindex, $element_default_selected_val, $elementnumber){\r\n		$i = count($this->stack);\r\n		$this->stack[$i][''name''] = $element_name;\r\n		$this->stack[$i][''type''] = $element_type;\r\n		$this->stack[$i][''default_value''] = $element_default;\r\n		$this->stack[$i][''validate_rule''] = $element_validate;\r\n		$this->stack[$i][''label''] = $this->_strip_slashes($element_label);\r\n		$this->stack[$i][''value''] = $this->get_value($element_type, $element_name, $element_default, $element_default_selected_val,\r\n														(in_array($element_type, array(''checkbox'', ''checkboxSelected'', ''optionMutiple'') )));\r\n		$this->stack[$i][''opt_attributes_label''] = $this->_strip_slashes($attributes_label);\r\n		$this->stack[$i][''opt_attributes_formelement''] = $this->_strip_slashes($attributes_formelement);\r\n		$this->stack[$i][''errormessage''] = $this->_strip_slashes($element_errormessage);\r\n		$this->stack[$i][''tpl_index''] = $element_tplindex;\r\n		$this->stack[$i][''elementnumber''] = $elementnumber;\r\n		//$this->stack[$i][''selected_value''] = $element_default_selected_val;\r\n		//some fields have advanced informations, like the upload \r\n		$this->stack[$i][''advanced''] = false;\r\n	}\r\n	\r\n	//set anonymous hidden vars\r\n	function set_anonymus_hidden($name, $value) {\r\n		$this->hiddenvalues .= $this->get_formelement($name, ''hidden'',  $value, '''');\r\n	}\r\n	\r\n	//debugmessage\r\n	function set_debug($d){ $this->debug = $d; }\r\n	\r\n	//final template to combinate all elements\r\n	function set_combination_tpl($tpl) { $this->combination_tpl= $tpl; }\r\n	\r\n	//message block template\r\n	function set_message_block_tpl($tpl) { $this->message_block_tpl= $tpl; }\r\n	\r\n	//template for the form element seperator \r\n	function set_separator_tpl($tpl) { $this->separator_tpl= $tpl; }\r\n	\r\n	//template for one form row\r\n	function set_row_tpl($index, $tpl) { $this->row_tpl[$index] = $tpl; }\r\n	\r\n	//template for formstarttag\r\n	function set_formstart_tpl($tpl){ $this->tpl_formstarttag= $tpl;}\r\n	\r\n	//template for formendttag\r\n	function set_formend_tpl($tpl){ $this->tpl_formendtag= $tpl;}\r\n	\r\n	//template for elementerror\r\n	function set_tpl_elementerror($tpl){ $this->tpl_elementerror= $tpl;}\r\n	\r\n	//url for form- action- parameter\r\n	function set_form_target_url($url){ $this->form_target_url= $url;}\r\n	\r\n	//email sender mode\r\n	function set_email_receiptor_mode($mode){ $this->email_receiptor_mode= $mode;}\r\n	\r\n	//settings for email sender mode ''get''\r\n	function set_email_receiptor_get_mode_data($idcatside, $container){ \r\n		$this->email_receiptor_get_mode_idcatside= $idcatside;\r\n		$this->email_receiptor_get_mode_idcontainer= $container;	\r\n	}\r\n	\r\n	//settings for email sender mode ''textfield''\r\n	function set_email_receiptor_textfield_mode_data($mailadress){ \r\n		$this->email_receiptor_textfield_mode_mailadress= $mailadress;\r\n	}\r\n\r\n	//emailadress sender\r\n	function set_email_sender($mailadress){ $this->email_sender= $mailadress;}\r\n\r\n	//email subject\r\n	function set_email_subject($s){$this->email_subject = $s;}\r\n	\r\n	//success message\r\n	function set_form_message_success($message){ $this->message_success= $message;}\r\n	\r\n	//error message\r\n	function set_form_message_error($message){ $this->message_error= $message;}\r\n	\r\n	//submitbutton text\r\n	function set_submitbutton_text($txt){ $this->submitbutton_text= $txt;}\r\n	\r\n	//mutiple value separator\r\n	function set_mutiple_value_separator($sep){ $this->mutiple_value_separator= $sep;}\r\n	\r\n	//key/ value value separator\r\n	function set_key_value_separator($sep){ $this->key_value_separator= $sep;}\r\n	\r\n	//upload dir\r\n	function set_upload_dir($dir){ $this->upload_dir= $dir;}\r\n	\r\n	//upload restrictions\r\n	function set_upload_restrictions($a){ \r\n		$this->upload_allowed_extensions= $a[''upload_allowed_extensions''];\r\n		$this->upload_allowed_filesize= $a[''upload_allowed_filesize''];\r\n	}\r\n	\r\n	//upload errors\r\n	function set_upload_errors($a){\r\n		$this->upload_errors[''usr_allowed_ext''] = $a[''usr_allowed_ext''];\r\n		$this->upload_errors[''usr_allowed_size''] = $a[''usr_allowed_size''];\r\n		$this->upload_errors[''usr_internal_error''] = $a[''usr_internal_error''];\r\n	}\r\n	\r\n	//fallback {textfromdynamic}\r\n	function set_fallback_textfromdynamic($in){ $this->fallback_textfromdynamic= $in;}\r\n\r\n	//fallback {textfarearomdynamic}\r\n	function set_fallback_textareafromdynamic($in){ $this->fallback_textareafromdynamic= $in;}\r\n	\r\n	function set_charset($set){ $this->charset = strtolower($set); }\r\n	\r\n	function set_tpl_email($tpl){ $this->tpl_email = $tpl; }\r\n	\r\n	function set_send_mail($set) {\r\n		$this->send_mail = (boolean) $set;\r\n	}\r\n	\r\n	function set_logging_active($set) {\r\n		$this->logging_active = (boolean) $set;\r\n	}\r\n	\r\n	function set_logging_file($set) {\r\n		$this->logging_file = $set;\r\n	}\r\n	\r\n	function set_email_receiptor_cc($set) { $this->email_receiptor_cc = $set; }\r\n	function set_email_receiptor_bcc($set) { $this->email_receiptor_bcc = $set; }\r\n	\r\n	function set_confirmmail_vars($is_active, $subject, $tpl) {\r\n		$this->confirm_mail_active = (boolean) $is_active;\r\n		$this->confirm_mail_subject = $subject;\r\n		$this->confirm_mail_tpl = $tpl;	\r\n	}\r\n	\r\n	function set_addon($addonstring) {\r\n		if (trim($addonstring) != '''') {\r\n			$p = explode(''||'', $addonstring);\r\n			if (is_file($p[''0''])) {\r\n				include_once $p[''0''];\r\n				if (class_exists($p[''1''])) {\r\n					$this->addon =& new $p[''1''];\r\n				}\r\n			}\r\n		}\r\n	}\r\n	\r\n	function set_captcha_vals($length = 4, $width = 100, $height = 30, $attributes = '''', $styleset = '''', $template = ''{image} {textfield}{hiddentoken}'') {\r\n		$this->captchavals[''length''] = $length;\r\n		$this->captchavals[''width''] = $width;\r\n		$this->captchavals[''height''] = $height;\r\n		$this->captchavals[''attributes''] = $attributes;\r\n		$this->captchavals[''styleset''] = $styleset;\r\n		$this->captchavals[''template''] = $template