' : null;\r\n		\r\n	}\r\n	\r\n	function _get_styled_message($message, $style){\r\n		if($c = $this->_format_styleclass($style) )\r\n			return ''<span ''.$c.''>''.$message.''</span>'';\r\n		\r\n		return $message; 	\r\n	}\r\n	\r\n	function _log($message) {\r\n		if (! $this->logging_active) {\r\n			return false;\r\n		}\r\n		\r\n		$fp = @fopen($this->logging_file, ''a+'');\r\n		@fputs($fp, $message);\r\n		@fclose($fp);\r\n	}\r\n	\r\n}}\r\n\r\n/* EXPERIMENTAL */\r\nif(! class_exists(gooseberry_form_addon)){\r\nclass gooseberry_form_addon {\r\n	function onPreParse(&$current, &$stack)\r\n	{\r\n		\r\n	}\r\n	\r\n	function onValidate(&$current, &$is_valid, &$stack)\r\n	{\r\n		\r\n	}\r\n	\r\n	function onParseRow(&$current, &$label, &$element, &$element_plain, &$stack)\r\n	{\r\n		\r\n	}\r\n	\r\n	function onFailure(&$stack)\r\n	{\r\n		\r\n	}\r\n	\r\n	function onSuccess(&$stack)\r\n	{\r\n\r\n	}\r\n}}\r\n\r\nif (! class_exists(gooseberry_formcontroller)) {\r\nclass gooseberry_formcontroller {\r\n	var $frm;//form object\r\n	var $content;//form html content\r\n	var $dbdata;// dataitem object\r\n	var $dbmeta;// reportdesc object\r\n\r\n\r\n	var $db_mode_active = false;\r\n	var $db_mode_default_datagroup;\r\n	var $mutiple_value_separator = '','';\r\n	var $redirect_after_success = false;\r\n	var $redirect_after_success_url = '''';\r\n	\r\n	var $save_in_filemanager = false;\r\n	var $filemanager_iddirectory = false;\r\n	\r\n	\r\n	function gooseberry_formcontroller() {\r\n		global $con_side;\r\n		if ((int)$_REQUEST[''rvbackidcatside''] >0) {\r\n			$this->redirect_after_success = true;\r\n			$this->redirect_after_success_url = $con_side[(int) $_REQUEST[''rvbackidcatside'']][''link''];\r\n			$parms = '''';\r\n			$parms .= ''&rvsortfield=''. $_REQUEST[''rvsortfield''];\r\n			$parms .= ''&rvstart=''. $_REQUEST[''rvstart''];\r\n			$parms .= ''&rvsortdirec=''. $_REQUEST[''rvsortdirec''];\r\n			$parms .= ''&rvsearchquery=''. $_REQUEST[''rvsearchquery''];\r\n			if (is_array($_REQUEST[''rvkeyfilter''])) {\r\n				foreach($_REQUEST[''rvkeyfilter''] AS $k=>$v) {\r\n					$parms .= ''&rvkeyfilter[''.$k.'']=''. $v;\r\n				}\r\n			}\r\n			if (is_array($_REQUEST[''rvfreefilter''])) {\r\n				foreach($_REQUEST[''rvfreefilter''] AS $k=>$v) {\r\n					$parms .= ''&rvfreefilter[''.$k.'']=''. $v;\r\n				}\r\n			}\r\n			$this->redirect_after_success_url .= $parms;\r\n			\r\n			//$this->redirect_after_success_url;exit;\r\n		}\r\n	}\r\n	\r\n	function set_formobject(&$frm) {\r\n		$this->frm =& $frm;\r\n	}\r\n	\r\n	function set_db_mode_active($set) {\r\n		$this->db_mode_active = (boolean) $set;\r\n	}\r\n	\r\n	function set_db_mode_default_datagroup($set) {\r\n		$this->db_mode_default_datagroup = $set;\r\n	}\r\n	\r\n	function set_db_mode_items_are_online($set) {\r\n		$this->db_mode_items_are_online = (boolean) $set;\r\n	}\r\n	\r\n	function set_mutiple_value_separator($set) {\r\n		$this->mutiple_value_separator = $set;\r\n	}\r\n	\r\n	function set_save_in_filemanager($set) {\r\n		$this->save_in_filemanager = $set;	\r\n	}\r\n	\r\n	function set_filemanager_iddirectory($id) {\r\n		$this->filemanager_iddirectory = (int) $id;\r\n	}\r\n	\r\n	function set_tpl_labeltext($tpl) {\r\n		$this->tpl_labeltext = $tpl;\r\n	}\r\n	\r\n	function set_redirect_after_success($bol, $url) {\r\n		global $con_side;\r\n		\r\n		if (trim($url) == '''') {\r\n			return false;\r\n		}\r\n		\r\n		if ((int) $url > 0) {\r\n			$url = $con_side[(int) $url][''link''];\r\n		}\r\n		\r\n		$this->redirect_after_success = (boolean) $bol;\r\n		$this->redirect_after_success_url = $url;\r\n	}\r\n	\r\n	function start() {\r\n		global $mvars;\r\n		$rvidnew = $rvidedit = 0;\r\n		//dbmode\r\n		if ($this->db_mode_active) {\r\n			//check external db mode\r\n			$rvidedit = (int) $_REQUEST[''rvidedit''];\r\n			$rvidnew = (int) $_REQUEST[''rvidnew''];\r\n			$is_db_mode_and_new_record = false;\r\n			\r\n			//from reportviewer context\r\n			if ($rvidedit > 0 || $rvidnew > 0) {\r\n				if ($rvidnew > 0) {\r\n					$this->dbmeta =& new rv_Reportdesc;\r\n					$this->dbmeta->loadByIddescreption($rvidnew);\r\n					$this->dbdata =& new rv_DataItem;\r\n					$this->dbdata->setDatagroup($this->dbmeta->getGroupname());\r\n				} else {\r\n					$this->dbdata =& new rv_DataItem;\r\n					$this->dbdata->loadByIdreportdata($rvidedit);\r\n					$this->dbmeta =& new rv_Reportdesc;\r\n					$this->dbmeta->loadByGroupname($this->dbdata->getDatagroup());\r\n				}\r\n				$is_db_mode_and_new_record = $this->dbdata->isEmptyRecord();\r\n				for ($iter =& $this->dbdata->getFormvalueIterator(); $iter->valid(); $iter->next() ) {\r\n		        	$formkey = ($iter->key()-1);\r\n		        	$fieldtype = $mvars[($formkey*20+101)];\r\n		        	switch ($fieldtype) {\r\n		        		case ''option'':\r\n						case ''optionMutiple'':\r\n						case ''radio'':\r\n						case ''checkbox'':\r\n						case ''checkboxSelected'':\r\n		        			$mvars[($formkey*20+109)] = $iter->current();\r\n		        			break;\r\n		        		default:\r\n		        			$mvars[($formkey*20+103)] = $iter->current();\r\n		        	}			\r\n				}\r\n			} else if ($this->db_mode_default_datagroup != ''''){\r\n				//from default\r\n				$this->dbdata =& new rv_DataItem;\r\n				$this->dbdata->setDatagroup($this->db_mode_default_datagroup);\r\n				\r\n				$this->dbmeta =& new rv_Reportdesc;\r\n				$this->dbmeta->loadByGroupname($this->db_mode_default_datagroup);\r\n				$is_db_mode_and_new_record = true;\r\n			} else {\r\n				$this->db_mode_active = false;\r\n			}\r\n			\r\n			//new record is per default online or offline\r\n			if ($is_db_mode_and_new_record) {\r\n				$this->dbdata->setIs_online($this->db_mode_items_are_online);\r\n			}\r\n		}\r\n		\r\n		//Formular erstellen\r\n		//n+0 aktiv\r\n		//n+1 type\r\n		//n+2 label\r\n		//n+3 default\r\n		//n+4 validate\r\n		//n+5 attributes label\r\n		//n+6 attributes formelement\r\n		//n+7 custom element- error\r\n		//n+8 index tpl elementcontainer\r\n		//n+9 default selected\r\n		//elementnumber $i/100\r\n		for($i=100; array_key_exists(($i+1), $mvars); $i=$i+20){	\r\n			if($mvars[$i]){\r\n				if ($mvars[$i+1] == ''checkboxSelected'' && $is_db_mode_and_new_record) {\r\n					$mvars[$i+9] = $mvars[$i+3];\r\n				}\r\n				$this->frm->set_formelement(''go_frm''.$i, $mvars[$i+1], $mvars[$i+3], $mvars[$i+4], $mvars[$i+2],\r\n						$mvars[$i+6], $mvars[$i+5], $mvars[$i+7], $mvars[$i+8], $mvars[$i+9], (($i/20)-4));\r\n			}\r\n		}\r\n		\r\n\r\n		//hidden vars by reportviewer\r\n		//sortfield=2&sortdirec=asc&start=0&rvidedit=7&rvbackidcatside=8 (edit)\r\n		//sortfield=2&sortdirec=asc&start=0&rvidnew=-1&rvbackidcatside=8 (new)\r\n		if ($this->db_mode_active) {\r\n			if ($rvidedit > 0 || $rvidnew == -1) {\r\n				$this->frm->set_anonymus_hidden(''rvsortfield'', $_REQUEST[''rvsortfield'']);\r\n				$this->frm->set_anonymus_hidden(''rvsortdirec'', $_REQUEST[''rvsortdirec'']);\r\n				$this->frm->set_anonymus_hidden(''rvsearchquery'', $_REQUEST[''rvsearchquery'']);\r\n				$this->frm->set_anonymus_hidden(''rvstart'', (int) $_REQUEST[''rvstart'']);\r\n				$this->frm->set_anonymus_hidden(''rvbackidcatside'', (int) $_REQUEST[''rvbackidcatside'']);\r\n				if (is_array($_REQUEST[''rvkeyfilter''])) {\r\n					foreach($_REQUEST[''rvkeyfilter''] AS $k=>$v) {\r\n						$this->frm->set_anonymus_hidden(''rvkeyfilter[''.$k.'']'', $this->frm->_strip_slashes($v));\r\n					}\r\n				}\r\n				if (is_array($_REQUEST[''freefilter''])) {\r\n					foreach($_REQUEST[''freefilter''] AS $k=>$v) {\r\n						$this->frm->set_anonymus_hidden(''freefilter[''.$k.'']'', $this->frm->_strip_slashes($v));\r\n					}\r\n				}\r\n			}\r\n			if ($rvidedit > 0) {\r\n				$this->frm->set_anonymus_hidden(''rvidedit'', (int) $_REQUEST[''rvidedit'']);\r\n			} else if ($rvidnew == -1) {\r\n				$this->frm->set_anonymus_hidden(''rvidnew'', (int) $_REQUEST[''rvidnew'']);\r\n			}\r\n		}\r\n		\r\n		$this->frm->parse_elements();\r\n		if ($this->frm->is_submitted()){\r\n			if ($this->frm->is_validated()){\r\n				$this->frm->send_mail();\r\n				$this->_reportviewer_save()