 if ($this->save_in_filemanager == ''rename'') {\r\n							$f->setOverwriteExistingFile(false);\r\n							$f->setAutorenameIfExists(true);\r\n						} else {\r\n							continue;\r\n						}\r\n						$f->setExternalSourceFile($v[''advanced''][''file''], $v[''advanced''][''type'']);\r\n						$f->setIddirectory($this->filemanager_iddirectory);\r\n						$f->save();\r\n						$v[''value''] = $f->getCmsLink();\r\n					}\r\n				default:\r\n					//special handling for ''option'' and ''optionMutiple''\r\n					$this->dbdata->setFormvalueByFormkey(($k+1), $v[''value'']);\r\n					array_push($used_keys, ($k+1));\r\n					$this->dbmeta->setFieldlabelByFieldkey(($k+1), $v[''label'']);\r\n\r\n			} \r\n		}\r\n		\r\n\r\n		$this->dbmeta->deleteFieldkeysByKeysNotInArray($used_keys);\r\n		\r\n		\r\n		$this->dbdata->save();\r\n		$this->dbmeta->save();\r\n		//print_r($this->dbmeta);\r\n	}\r\n	\r\n	function process_redirect() {\r\n		if(! $this->redirect_after_success) {\r\n			return false;\r\n		}\r\n		//echo $this->redirect_after_success_url;exit;\r\n		sf_header_redirect($this->redirect_after_success_url);\r\n	}\r\n	\r\n}}\r\n\r\n//\r\n// START WORKING\r\n//\r\n\r\n//look for reportviewer plugin\r\nif (is_file($cfg_cms[''cms_path''].''plugins/reportviewer/index.php'')) {\r\n	$mvars[''reportviewer_included''] = true;\r\n	include_once $cfg_cms[''cms_path''].''plugins/reportviewer/includes/class.rv_ArrayIterator.php'';\r\n	include_once $cfg_cms[''cms_path''].''plugins/reportviewer/includes/class.rv_DataItem.php'';\r\n	include_once $cfg_cms[''cms_path''].''plugins/reportviewer/includes/class.rv_Reportdesc.php'';\r\n} else {\r\n	$mvars[''reportviewer_included''] = false;\r\n}\r\ninclude_once $cfg_cms[''cms_path''].''external/misc/class.captcha.php'';\r\n$frm = new gooseberry_form;\r\n//set debug\r\n$frm->set_debug( ($mvars[''44''] == ''true'') ? true: false);\r\n//set form styles\r\n$frm->set_styles (array(   \r\n   ''submit_error'' => $mvars[''15''],\r\n   ''submit'' => $mvars[''16''],\r\n   ''lab