active][''desc''] = ''Template wenn Sprachen-Elemente verfügbar und Aktiv ist (wird pro sprache in {content} eingefügt):<br><small>Mögliche Templatevariablen: {font1}, {font2}, {font3}, {url}, {idlang}, {name}, {description}, {rewrite_key}, {id}, {parity}</small>'';\r\n		$mip_form[$frm_lang_active][''cms_var''] = "MOD_VAR[$frm_lang_active]";\r\n		$mip_form[$frm_lang_active][''cms_val''] = $cms_mod[''value''][$frm_lang_active];\r\n		if ($cms_mod[''value''][''21''] == '''') {\r\n			$mip_form[$frm_lang_active][''cms_val_default''] = $mip_form[''21''][''cms_val_default''];\r\n		} else {\r\n			$mip_form[$frm_lang_active][''cms_val_default''] = $cms_mod[''value''][''21''];\r\n		}\r\n		$mip_form[$frm_lang_active][''tab''] = ''0'';\r\n\r\n		// template lang not available \r\n		$mip_form[$frm_lang_not_available][''cat''] = ''txtarea'';\r\n		$mip_form[$frm_lang_not_available][''rows''] = ''3'';\r\n		$mip_form[$frm_lang_not_available][''type''] = ''long''; \r\n		$mip_form[$frm_lang_not_available][''desc''] = ''Template wenn Sprachen-Elemente nicht verfügbare  (wird pro sprache in {content} eingefügt):<br><small>Mögliche Templatevariablen: {font1}, {font2}, {font3}, {idlang}, {name}, {description}, {rewrite_key}, {id}, {parity}</small>'';\r\n		$mip_form[$frm_lang_not_available][''cms_var''] = "MOD_VAR[$frm_lang_not_available]";\r\n		$mip_form[$frm_lang_not_available][''cms_val''] = $cms_mod[''value''][$frm_lang_not_available];\r\n		if ($cms_mod[''value''][''22''] == '''') {\r\n			$mip_form[$frm_lang_not_available][''cms_val_default''] = $mip_form[''22''][''cms_val_default''];\r\n		} else {\r\n			$mip_form[$frm_lang_not_available][''cms_val_default''] = $cms_mod[''value''][''22''];\r\n		}\r\n		$mip_form[$frm_lang_not_available][''tab''] = ''0'';\r\n\r\n		mip_forms_tabitem_beginp(''Sprache ''.($i+1));\r\n			mip_formsp($mip_form[$frm_view]);\r\n			mip_formsp($mip_form[$frm_lang]);\r\n			mip_formsp($mip_form[$frm_lang_active]);\r\n			mip_formsp($mip_form[$frm_lang_available]);\r\n			mip_formsp($mip_form[$frm_lang_not_available]);\r\n		mip_forms_tabitem_endp();\r\n\r\n		$ecount = $ecount+10;\r\n	}\r\n}\r\n\r\nunset($mip_form);\r\n?>', '<CMSPHP>\r\n/******************************************************************************\r\n @author:	Andreas Lechner (cliff), Peter Fuchs (renard)\r\n @link		http://www.cms4you.at\r\n******************************************************************************/\r\n</CMSPHP>\r\n\r\n<CMSPHP:CACHE>\r\necho ''<CMSPHP>'';\r\nif (is_array($cms_mod[''value''])) {\r\n	foreach($cms_mod[''value''] AS $k => $v){\r\n		echo ''$mvars["''.$k.''"] = "''. addslashes($v) .''";''."\\n";\r\n	}\r\n}\r\necho ''</CMSPHP>'';\r\n</CMSPHP:CACHE>\r\n\r\n<CMSPHP>\r\n// get idcatside\r\nif ($mvars[''5''] != '''') {\r\n	$newidcatside = $mvars[''5''];\r\n} else {\r\n	$newidcatside = $idcatside;\r\n}\r\n\r\nif (!is_array($langs_data)) {\r\n\r\n	$sql = "SELECT b.idlang, b.name, b.description, b.rewrite_key FROM  ".$cms_db[''clients_lang'']." AS a, ".$cms_db[''lang'']." AS b WHERE a.idlang=b.idlang AND a.idclient=''".$client."''";\r\n\r\n	$db->query($sql);\r\n	if ($db->affected_rows()) {\r\n		while ($db->next_record()) {\r\n			$langs_data[$db->f(''idlang'')][''idlang'']			= $db->f(''idlang'');\r\n			$langs_data[$db->f(''idlang'')][''name'']			= htmlentities($db->f(''name''), ENT_COMPAT, ''utf-8'');\r\n			$langs_data[$db->f(''idlang'')][''description'']	= htmlentities($db->f(''description''), ENT_COMPAT, ''utf-8'');			\r\n			$langs_data[$db->f(''idlang'')][''rewrite_key'']	= htmlentities($db->f(''rewrite_key''), ENT_COMPAT, ''utf-8'');			\r\n		}\r\n	}\r\n}\r\n\r\nif (!is_array($langs_online)) {\r\n\r\n	$sql2 = " AND (  (b.online & 0x03) = 0x01 OR (  (b.online & 0x02) = 0x02 AND ( UNIX_TIMESTAMP(NOW()) BETWEEN b.start AND b.end))) ";\r\n	$sql2 .=  ( $perm->have_perm(2, ''area_frontend'', 0) ) ?  '''': '' AND (b.online & 0x04) = 0x00 '';\r\n	$sql = "SELECT b.idlang, c.name FROM ((".$cms_db[''cat_side'']." AS a INNER JOIN ".$cms_db[''side_lang'']." AS b ON a.idside = b.idside) INNER JOIN ".$cms_db[''lang'']." AS c ON b.idlang = c.idlang) INNER JOIN ".$cms_db[''side'']. " AS d ON a.idside = d.idside WHERE (a.idcatside=" . $newidcatside. " ) AND (d.idclient=" . $client . ") ".$sql2;\r\n	\r\n	$db->query($sql);\r\n	if ($db->affected_rows()) {\r\n		while ($db->next_record()) {\r\n			$langs_online[] = $db->f(''idlang'');\r\n		}\r\n	}\r\n}\r\n\r\n// create link\r\nif (!function_exists(''createLinkUrl'')) {\r\n	function createLinkUrl($langid, $newidcatside, $is_online) {\r\n		global $cfg_client, $sess, $view;\r\n		// backend view\r\n		if (isset($view) && $view != "") {\r\n			if ($is_online == "1") {\r\n				return $sess->url($cfg_client["contentfile"].''?idcatside=''.$newidcatside.''&lang=''.$langid.''&view=''.$view);\r\n			}\r\n		// frontend view\r\n		} else {\r\n			if ($is_online == "1") {\r\n				// apache mod_rewrite support = 1\r\n				if ($cfg_client[''url_rewrite''] == ''1'') {\r\n					$url_rewrite_in = array(\r\n						"''(?<!/)".$cfg_client[''contentfile'']."\\?idcat=([1-9][0-9]*)(&|&amp;)lang=([1-9][0-9]?)''",\r\n						"''(?<!/)".$cfg_client[''contentfile'']."\\?idcatside=([1-9][0-9]*)(&|&amp;)lang=([1-9][0-9]?)''"\r\n					);\r\n					$url_rewrite_out = array(\r\n						"cat\\\\1-\\\\3.html",\r\n						"page\\\\1-\\\\3.html"\r\n					);\r\n					$linkUrl = $sess->url($cfg_client["contentfile"].''?idcatside=''.$newidcatside.''&amp;lang=''.$langid);\r\n					$linkUrl = preg_replace($url_rewrite_in, $url_rewrite_out, $linkUrl);\r\n				// apache mod_rewrite support = 2\r\n				} elseif ($cfg_client[''url_rewrite''] == ''2'') {\r\n					$linkUrl = rewriteGetPageUrl($newidcatside, $langid, true);\r\n				} else {\r\n					$linkUrl = $sess->url($cfg_client["contentfile"].''?idcatside=''.$newidcatside.''&amp;lang=''.$langid);\r\n				}\r\n\r\n				return $linkUrl;\r\n			}\r\n		}\r\n	}\r\n}\r\n\r\n// process element template\r\n$elements = '''';\r\n$ecount = 100;\r\n$my_langs_all = count($langs_data);\r\n\r\nfor ($index = 0; $index < $my_langs_all; $index++) {\r\n\r\n	$frm_view				= $ecount;\r\n	$frm_lang				= $ecount+1;\r\n	$frm_lang_available		= $ecount+2;\r\n	$frm_lang_active		= $ecount+3;\r\n	$frm_lang_not_available = $ecount+4;\r\n\r\n	$my_flag_sort = false;\r\n\r\n	// sort\r\n	if ((isset($langs_data [$mvars[$frm_lang]][''idlang''])) && ($mvars[$frm_view] == 1 || (isset($cms_side[''view'']) && $mvars[''8''] == ''true''))) {\r\n		// from config\r\n		$my_flag_sort = true;\r\n		$my_lang_id = $mvars[$frm_lang];\r\n	} elseif($mvars[''7''] == ''true'' || (isset($cms_side[''view'']) && $mvars[''8''] == ''true'' && $mvars[''12''] == ''true'')) {\r\n		// from backend\r\n		$my_array_key = array_keys($langs_data);\r\n		$my_lang_id = $my_array_key[0]; \r\n	} else {\r\n		$my_lang_id = '''';\r\n	}\r\n\r\n	// lang available\r\n	if (((!empty($langs_online) && (in_array($my_lang_id, $langs_online))) || (isset($cms_side[''view'']) && ( ($mvars[''7''] == ''true'' && ($mvars[''12''] == ''true'' || $my_flag_sort == true)) || $mvars[''8''] == ''true''))) && $my_lang_id != '''') {\r\n		$is_available = 1;\r\n	} else {\r\n		$is_available = 0;\r\n	}\r\n\r\n	$my_flag_count = false;\r\n	\r\n	// what template\r\n	if ($mvars[''6''] == ''true'' && $my_lang_id == $lang && $is_available == 1) {\r\n\r\n		$my_flag_count = true;\r\n\r\n		if ($mvars[''11''] == ''true'' || ($mvars[''12''] == ''true'' && !$my_flag_sort)) {\r\n			// master tpl\r\n			$template = $mvars[''21''];\r\n		} else {\r\n			$template = $mvars[$frm_lang_active];\r\n		}\r\n\r\n	} elseif (($my_lang_id != $lang && $is_available == 1) || ($my_lang_id != $lang && $is_available == 1 && isset($cms_side[''view'']) && ( ($mvars[''7''] == ''true'' && ($mvars[''12''] == ''true'' || $my_flag_sort == true)) || $mvars[''8''] == ''true'') )) {\r\n\r\n		$my_flag_count = true;\r\n\r\n		if ($mvars[''11''] == ''true'' || ($mvars[''12''] == ''true'' && !$my_flag_sort)) {\r\n			// master tpl\r\n			$template = $mvars[''20''];\r\n		} else {\r\n			$template = $mvars[$frm_lang_available];\r\n		}\r\n\r\n	} elseif ($is_available == 0 && ($mvars[''7''] == ''true'' && ($mvars[''12''] == ''true'' || $my_flag_sort == true))) {\r\n\r\n		$my_flag_count = true;\r\n\r\n		if ($mvars[''11''] == ''true'' || ($mvars[''12''] == ''true'' && !$my_flag_sort)) {\r\n			// master tpl\r\n			$template = $mvars[''22''];\r\n		} else {\r\n			$template = $mvars[$frm_lang_not_available];\r\n		}\r\n\r\n	} else {\r\n		$template = '''';\r\n	}\r\n\r\n	if ($my_flag_count) {\r\n		if ($index == 0) {\r\n			$my_counter = 1;\r\n		} else {\r\n			$my_counter++;\r\n		}\r\n	}\r\n\r\n	if ($my_counter % 2 != 0) {\r\n		$my_parity = ''odd'';\r\n	} else {\r\n		$my_parity = ''even'';\r\n	}\r\n\r\n	$template = str_replace("{font1}", $mvars[''1''], $template);\r\n	$template = str_replace("{font2}", $mvars[''2''], $template);\r\n	$template = str_replace("{font3}", $mvars[''3''], $template);\r\n	$template = str_replace("{url}", createLinkUrl($my_lang_id, $newidcatside, $is_available), $template);\r\n	$template = str_replace("{idlang}", $langs_data[$my_lang_id][''idlang''], $template);\r\n	$template = str_replace("{name}", $langs_data[$my_lang_id][''name''], $template);\r\n	$template = str_replace("{description}", $langs_data[$my_lang_id][''description''], $template);\r\n	$template = str_replace("{rewrite_key}", $langs_data[$my_lang_id][''rewrite_key''], $template);\r\n	$template = str_replace("{id}", $my_counter, $template);\r\n	$template = str_replace("{parity}", $my_parity, $template);\r\n\r\n	$elements .= $template;\r\n\r\n	// dual language edition prevent\r\n	if ($my_lang_id != '''') {\r\n		unset($langs_data[$my_lang_id]);\r\n	}\r\n\r\n	$ecount = $ecount+10;\r\n}\r\n\r\n// process global template\r\n$globaltemplate = str_replace("{content}", $elements, $mvars[''10'']);\r\necho $globaltemplate;\r\nunset($globaltemplate,$newidcatside,$elements,$template,$is_available,$langs_data,$langs_online,$sql,$mvars);\r\n</CMSPHP>', '', 0, 1, 1, 1066927900, 1215637017, 'mod:d3cac1d21bfb93f3a602c39