INSERT INTO `cms_mod` VALUES (16, 'Login', '2.2', 'System', 'Login/out-Formular für die geschützten Bereiche.', '<?PHP\r\n//val ref\r\n// 01 txt Datumsformat\r\n// 02 txt Zeitformat\r\n// 03 txtarea Template für die Fehlermeldung\r\n// 06 txt attribute benutzername\r\n// 07 txt attribute passwort\r\n// 08 Template Anmeldung\r\n// 09 Template Logout\r\n// 10 Frontendbearbeitung erlauben \r\n// 12 txt weiterleitung nach login\r\n// 13 txt weiterleitung nach logout\r\n// 14 txtarea tpl date\r\n// 15 txtarea tpl date_empty\r\n// 16 txt weiterleitung wenn eingeloggt\r\n// 17 txt weiterleitung wenn eingeloggt nicht im backend\r\n// 18 chkbox Login nur mit Benutzernamen (Benutzername und Passwort sind identisch) \r\n\r\n//Template Fehlermeldung\r\n$mip_form[''3''][''desc''] = ''Template Fehlermeldung bei ungültigem Login'';\r\n$mip_form[''3''][''type''] = ''long'';\r\n$mip_form[''3''][''rows''] = ''3'';\r\n$mip_form[''3''][''cat''] = ''txtarea'';\r\n$mip_form[''3''][''cms_var''] = ''MOD_VAR[3]'';\r\n$mip_form[''3''][''cms_val''] = $cms_mod[''value''][''3''];\r\n$mip_form[''3''][''cms_val_default''] = ''<tr>\r\n    <td colspan="2">Login fehlgeschlagen! Falscher Benutzername oder falsches Passwort!</td>\r\n</tr>'';\r\n$mip_form[''3''][''tab''] = ''0'';\r\n\r\n//attribute benutzername\r\n$mip_form[''6''][''desc''] = ''Optionale Formularattribute f&uuml;r Benutzername'';\r\n$mip_form[''6''][''cat''] = ''txt'';\r\n$mip_form[''6''][''cms_var''] = ''MOD_VAR[6]'';\r\n$mip_form[''6''][''cms_val''] = $cms_mod[''value''][''6''];\r\n$mip_form[''6''][''cms_val_default''] = '''';\r\n\r\n//attribute passwort\r\n$mip_form[''7''][''desc''] = ''Optionale Formularattribute f&uuml;r Passwort'';\r\n$mip_form[''7''][''cat''] = ''txt'';\r\n$mip_form[''7''][''cms_var''] = ''MOD_VAR[7]'';\r\n$mip_form[''7''][''cms_val''] = $cms_mod[''value''][''7''];\r\n$mip_form[''7''][''cms_val_default''] = '''';\r\n\r\n//Datumsformat \r\n$mip_form[''1''][''desc''] = ''Formatierung Datum letzter Login'';\r\n$mip_form[''1''][''cat''] = ''txt'';\r\n$mip_form[''1''][''cms_var''] = ''MOD_VAR[1]'';\r\n$mip_form[''1''][''cms_val''] = $cms_mod[''value''][''1''];\r\n$mip_form[''1''][''cms_val_default''] = ''d.m.Y'';\r\n\r\n//Zeitformat\r\n$mip_form[''2''][''desc''] = ''Formatierung Uhrzeit letzter Login'';\r\n$mip_form[''2''][''cat''] = ''txt'';\r\n$mip_form[''2''][''cms_var''] = ''MOD_VAR[2]'';\r\n$mip_form[''2''][''cms_val''] = $cms_mod[''value''][''2''];\r\n$mip_form[''2''][''cms_val_default''] = ''H:i:s'';\r\n\r\n// Template Anmeldung\r\n$mip_form[''8''][''cat''] = ''txtarea'';\r\n$mip_form[''8''][''type''] = ''long'';\r\n$mip_form[''8''][''rows''] = ''8'';\r\n$mip_form[''8''][''desc''] = ''Template<br><small>Mögliche Templatevariablen: {form_attributes}, {input_name}, {input_password}, {errortemplate}</small>'';\r\n$mip_form[''8''][''cms_var''] = ''MOD_VAR[8]'';\r\n$mip_form[''8''][''cms_val''] = $cms_mod[''value''][''8''];\r\n$mip_form[''8''][''cms_val_default''] = ''<form {form_attributes}>\r\n<table cellspacing="0" cellpadding="0" border="0">\r\n  {errortemplate}\r\n  <tr>\r\n    <td>&nbsp;Username :&nbsp;</td>\r\n    <td>&nbsp;{input_name}&nbsp;</td>\r\n  </tr>\r\n  <tr>\r\n    <td>&nbsp;Passwort :&nbsp;</td>\r\n    <td>&nbsp;{input_password}&nbsp;</td>\r\n  </tr>\r\n  <tr>\r\n    <td colspan="2" align="right"><input type="submit" name="submit" value="Einloggen" \\></td>\r\n  </tr>\r\n</table>\r\n</form>'';\r\n\r\n// Template Logout\r\n$mip_form[''9''][''cat''] = ''txtarea'';\r\n$mip_form[''9''][''type''] = ''long'';\r\n$mip_form[''9''][''rows''] = ''8'';\r\n$mip_form[''9''][''desc''] = ''Template Logout<br><small>Mögliche Templatevariablen: {name}, {surname}, {firm}, {position}, {salutation}, {street}, {zip}, {location}, {phone}, {fax}, {comment}, {email}, {lastlogin}, {form_attributes}, {form_hiddenvals}</small>'';\r\n$mip_form[''9''][''cms_var''] = ''MOD_VAR[9]'';\r\n$mip_form[''9''][''cms_val''] = $cms_mod[''value''][''9''];\r\n$mip_form[''9''][''cms_val_default''] = ''<form {form_attributes}>\r\n<table cellspacing="0" cellpadding="0" border="0">\r\n  <tr>\r\n    <td>Guten Tag {salutation} {name} {surname}!</td>\r\n  </tr>\r\n  <tr>\r\n    <td align="right"><input type="submit" name="submit" value="Ausloggen" /></td>\r\n  </tr>\r\n</table>\r\n{form_hiddenvals}\r\n</form>'';\r\n\r\n// Frontendbearbeitung erlauben\r\n$mip_form[''10''][''desc''] = ''Frontendbearbeitung erlauben'';\r\n$mip_form[''10''][''cat''] = ''chk'';\r\n$mip_form[''10''][''option_var''][''0''] = "MOD_VAR[10]";\r\n$mip_form[''10''][''option_val''][''0''] = $cms_mod[''value''][''10''];\r\n$mip_form[''10''][''option_desc''][''0''] = ''ja'';\r\n$mip_form[''10''][''option_val_select''][''0''] = ''true'';\r\n\r\n//Weiterleitung nach Login\r\n$mip_form[''12''][''desc''] = ''Nach Login weiterleiten auf Seite <small>(Idcatside angeben, Eingabe "0" = keine Weiterleitung)</small>'';\r\n$mip_form[''12''][''cat''] = ''txt'';\r\n$mip_form[''12''][''cms_var''] = ''MOD_VAR[12]'';\r\n$mip_form[''12''][''cms_val''] = $cms_mod[''value''][''12''];\r\n$mip_form[''12''][''cms_val_default''] = ''0'';\r\n\r\n//Weiterleitung nach Logout\r\n$mip_form[''13''][''desc''] = ''Nach Logout weiterleiten auf Seite <small>(Idcatside angeben, Eingabe "0" = keine Weiterleitung)</small>'';\r\n$mip_form[''13''][''cat''] = ''txt'';\r\n$mip_form[''13''][''cms_var''] = ''MOD_VAR[13]'';\r\n$mip_form[''13''][''cms_val''] = $cms_mod[''value''][''13''];\r\n$mip_form[''13''][''cms_val_default''] = ''0'';\r\n\r\n//Template Datum\r\n$mip_form[''14''][''desc''] = ''Template Letzter Login<br /><small>Templatevariablen: {lastlogin_date}, {lastlogin_time}</small>'';\r\n$mip_form[''14''][''type''] = ''long'';\r\n$mip_form[''14''][''rows''] = ''2'';\r\n$mip_form[''14''][''cat''] = ''txtarea'';\r\n$mip_form[''14''][''cms_var''] = ''MOD_VAR[14]'';\r\n$mip_form[''14''][''cms_val''] = $cms_mod[''value''][''14''];\r\n$mip_form[''14''][''cms_val_default''] = ''Letzter Login am {lastlogin_date}, um {lastlogin_time} Uhr'';\r\n$mip_form[''14''][''tab''] = ''0'';\r\n\r\n//Template Datum leer\r\n$mip_form[''15''][''desc''] = ''Template letzer Login, wenn kein Wert vorhanden'';\r\n$mip_form[''15''][''type''] = ''long'';\r\n$mip_form[''15''][''rows''] = ''2'';\r\n$mip_form[''15''][''cat''] = ''txtarea'';\r\n$mip_form[''15''][''cms_var''] = ''MOD_VAR[15]'';\r\n$mip_form[''15''][''cms_val''] = $cms_mod[''value''][''15''];\r\n$mip_form[''15''][''cms_val_default''] = ''Sie haben sich noch nie eingeloggt.'';\r\n$mip_form[''15''][''tab''] = ''0'';\r\n\r\n//Weiterleitung wenn eingeloggt\r\n$mip_form[''16''][''desc''] = ''Wenn eingeloggt, grundsätzlich weiterleiten auf Seite <small>(Idcatside angeben, Eingabe "0" = keine Weiterleitung, Weiterleitung gilt nur für das Frontend)</small>'';\r\n$mip_form[''16''][''cat''] = ''txt'';\r\n$mip_form[''16''][''cms_var''] = ''MOD_VAR[16]'';\r\n$mip_form[''16''][''cms_val''] = $cms_mod[''value''][''16''];\r\n$mip_form[''16''][''cms_val_default''] = ''0'';\r\n\r\n\r\n// Weiterleitung auch im Backend\r\n$mip_form[''17''][''desc''] = ''Wenn eingeloggt Weiterleitung auch im Backend'';\r\n$mip_form[''17''][''cat''] = ''chk'';\r\n$mip_form[''17''][''option_var''][''0''] = "MOD_VAR[17]";\r\n$mip_form[''17''][''option_val''][''0''] = $cms_mod[''value''][''17''];\r\n$mip_form[''17''][''option_desc''][''0''] = ''ja'';\r\n$mip_form[''17''][''option_val_select''][''0''] = ''true'';\r\n\r\n// Login nur mit Benutzernamen (Benutzername und Passwort sind identisch) \r\n$mip_form[''18''][''desc''] = ''Login nur mit Benutzernamen  <small>(Benutzername und Passwort sind identisch, es wird nur die Variable {input_name} geprüft)</small>'';\r\n$mip_form[''18''][''cat''] = ''chk'';\r\n$mip_form[''18''][''option_var''][''0''] = "MOD_VAR[18]";\r\n$mip_form[''18''][''option_val''][''0''] = $cms_mod[''value''][''18''];\r\n$mip_form[''18''][''option_desc''][''0''] = ''ja'';\r\n$mip_form[''18''][''option_val_select''][''0''] = ''true'';\r\n\r\n\r\nmip_formsp($mip_form[''12'']); //Weiterleitung nach login\r\nmip_formsp($mip_form[''16'']); //Weiterleitung wenn eingeloggt\r\nmip_formsp($mip_form[''17'']); //Weiterleitung wenn eingeloggt, auch im Backend\r\nmip_formsp($mip_form[''13'']); //Weiterleitung nach logout\r\nmip_formsp($mip_form[''10'']); //Frontendbearbeitung erlauben\r\nmip_formsp($mip_form[''18'']); //Login nur mit Benutzernamen\r\nmip_formsp($mip_form[''6'']);  //Attribute benutzername\r\nmip_formsp($mip_form[''7'']);  //Attribute passwort\r\nmip_formsp($mip_form[''8'']);  //Template Anmeldung  \r\nmip_formsp($mip_form[''3'']);  //Template für die Fehlermeldung\r\nmip_formsp($mip_form[''9'']);  //Template Logout\r\nmip_formsp($mip_form[''1'']);  //Datumsformat\r\nmip_formsp($mip_form[''2'']);  //Zeitformat\r\nmip_formsp($mip_form[''14'']); //Template Datum\r\nmip_formsp($mip_form[''15'']); //Template Datum leer\r\n\r\nunset($mip_form);\r\n?>', '<CMSPHP>\r\n$is_dev = false;\r\n\r\n//config\r\n$mod[''dateformat''] = "MOD_VALUE[1]";\r\n$mod[''timeformat''] = "MOD_VALUE[2]";\r\n$mod[''tpl_lastlogin''] = "MOD_VALUE[14]";\r\n$mod[''tpl_lastlogin_empty''] = "MOD_VALUE[15]";\r\n$mod[''tpl_login''] = "MOD_VALUE[8]";\r\n$mod[''tpl_login_error''] = "MOD_VALUE[3]";\r\n$mod[''tpl_logout''] = "MOD_VALUE[9]";\r\n$mod[''redirect_login''] = (int) trim("MOD_VALUE[12]");\r\n$mod[''redirect_logout''] = (int) trim("MOD_VALUE[13]");\r\n$mod[''redirect_if_logged_in_and backend''] = "MOD_VALUE[17]";\r\n$mod[''allow_backendlogin''] = "MOD_VALUE[10]";\r\n$mod[''attributes_username''] = "MOD_VALUE[6]";\r\n$mod[''attributes_password''] = "MOD_VALUE[7]";\r\n$mod[''redirect_if_logged_in''] = (int) "MOD_VALUE[16]";\r\n$mod[''username_is_password''] = "MOD_VALUE[18]";\r\n\r\n$mod[''throw_error''] = false;\r\n\r\nif ($is_dev) {\r\n   include_once "C:/Programme/xampp/htdocs/sefrengo_dev/moddev/login-out.php";\r\n} else {\r\n\r\n\r\nif ($_REQUEST[''action''] == ''login'') \r\n{\r\n	//write loginaction to log\r\n    $sf_wr =& $GLOBALS[''sf_factory'']->getObject(''HTTP'', ''WebRequest'');\r\n	$username = $sf_wr->getVal(''username''); \r\n	$password = ($mod[''username_is_password''] == ''true'') ? $username : $sf_wr->getVal(''password''); \r\n	$sf_user =& sf_factoryGetObject(''ADMINISTRATION'', ''User''); \r\n    $sf_user->loadByUsernamePassword($username, $password, true);\r\n    unset($sf_user);\r\n    \r\n	if ($username != '''') {\r\n        $auth->auth[''uname''] = $username;\r\n    } else if ($auth->nobody) {\r\n		$uid = $auth->auth[''uname''] = $auth->auth[''uid''] = ''nobody'';\r\n	}\r\n	\r\n    // User aus der Datenbank suchen\r\n	$username = addslashes($username);\r\n	$sql_password = ($mod[''username_is_password''] == ''true'') ? md5($username):md5($password);\r\n	$sql = "SELECT \r\n		firm, position, salutation, street, zip, location, phone, fax, comment, A.user_id, password, A.name, surname, email, C.name AS groupname, C.description\r\n		FROM ".$cms_db[''users'']." A\r\n		LEFT JOIN ".$cms_db[''users_groups'']." B USING(user_id)\r\n		LEFT JOIN ".$cms_db[''groups'']." C USING(idgroup)\r\n		LEFT JOIN ".$cms_db[''perms'']." D USING(idgroup)\r\n		WHERE A.username=''$username''\r\n		AND A.password=''".$sql_password."''\r\n		AND A.is_active=''1''\r\n		AND C.is_active=''1''\r\n		AND ((D.type=''cms_access'' AND D.id=''area_backend'' AND D.perm>0) OR C.is_sys_admin=''1'') LIMIT 0, 1";\r\n	$db->query($sql);\r\n	\r\n	//login to backend\r\n	if ($db->next_record() && $mod[''allow_backendlogin''] == ''true'') {\r\n		\r\n		// Backenduser Login\r\n		$auth->logout();\r\n		$sess = new cms_Backend_Session;\r\n		$auth = new cms_Backend_Auth;\r\n		$HTTP_GET_VARS[''cms''] = $_GET[''cms''] = md5(uniqid($sess->magic));\r\n		$sess->start();\r\n		$auth->auth[''name''] = $db->f(''name'');\r\n		$auth->auth[''surname''] = $db->f(''surname'');\r\n		$auth->auth[''group_name''] = $db->f(''groupname'');\r\n		$auth->auth[''group_desc''] = $db->f(''description'');\r\n		$auth->auth[''email''] = $db->f(''email'');\r\n		$auth->auth[''firm''] = $db->f(''firm'');\r\n		$auth->auth[''position''] = $db->f(''position'');\r\n		$auth->auth[''salutation''] = $db->f(''salutation'');\r\n		$auth->auth[''street''] = $db->f(''street'');\r\n		$auth->auth[''zip''] = $db->f(''zip'');\r\n		$auth->auth[''location''] = $db->f(''location'');\r\n		$auth->auth[''phone''] = $db->f(''phone'');\r\n		$auth->auth[''fax''] = $db->f(''fax'');\r\n		$auth->auth[''comment''] = $db->f(''comment'');\r\n		$auth->auth[''uid''] = $db->f(''user_id'');\r\n		$auth->auth[''uname''] = $username;\r\n		$auth->auth[''exp''] = time() + (60 * $auth->lifetime);\r\n		$auth->auth[''refresh''] = time() + (60 * $auth->refresh);\r\n		$sess->register(''auth'');\r\n		$sess->register(''sid_sniffer'');\r\n		$sess->freeze();\r\n\r\n		// redirect\r\n		$sf_idcatside_redirect = $mod[''redirect_login''];\r\n		if ($sf_idcatside_redirect < 1) {\r\n		  $sf_idcatside_redirect = $idcatside;\r\n		}\r\n\r\n		sf_header_redirect($cfg_client[''contentfile'']."?lang=$lang&idcatside=$sf_idcatside_redirect&view=preview&".$sess->name.''=''.$sess->id);\r\n	//login to frontend\r\n    } else {\r\n		$sql = "SELECT \r\n		firm, position, salutation, street, zip, location, phone, fax, comment, A.user_id, password, A.name, surname, email, C.name AS groupname, C.description\r\n		FROM ".$cms_db[''users'']." A\r\n		LEFT JOIN ".$cms_db[''users_groups'']." B USING(user_id)\r\n		LEFT JOIN ".$cms_db[''groups'']." C USING(idgroup)\r\n		LEFT JOIN ".$cms_db[''perms'']." D USING(idgroup)\r\n		WHERE A.username=''$username''\r\n		AND A.password=''".$sql_password."''\r\n		AND A.is_active=''1''\r\n		AND C.is_active=''1''\r\n		AND ((D.type=''cms_access'' AND D.id=''area_frontend'' AND D.perm=''1'') OR C.is_sys_admin=''1'') LIMIT 0, 1";\r\n		$db->query($sql);\r\n		if ($db->next_record()) {\r\n			// Frontenduser Login\r\n			$auth->auth[''name''] = $db->f(''name'');\r\n			$auth->auth[''surname''] = $db->f(''surname'');\r\n			$auth->auth[''group_name''] = $db->f(''groupname'');\r\n			$auth->auth[''group_desc''] = $db->f(''description'');\r\n			$auth->auth[''email''] = $db->f(''email'');\r\n			$auth->auth[''firm''] = $db->f(''firm'');\r\n			$auth->auth[''position''] = $db->f(''position'');\r\n			$auth->auth[''salutation''] = $db->f(''salutation'');\r\n			$auth->auth[''street''] = $db->f(''street'');\r\n			$auth->auth[''zip''] = $db->f(''zip'');\r\n			$auth->auth[''location''] = $db->f(''location'');\r\n			$auth->auth[''phone''] = $db->f(''phone'');\r\n			$auth->auth[''fax''] = $db->f(''fax'');\r\n			$auth->auth[''comment''] = $db->f(''comment'');\r\n			$auth->auth[''uid''] = $db->f(''user_id'');\r\n			$auth->auth[''uname''] = $username;\r\n			$auth->auth[''exp''] = time() + (60 * $auth->lifetime);\r\n			$auth->auth[''refresh''] = time() + (60 * $auth->refresh);\r\n			$sess->register(''auth'');\r\n			$sess->register(''sid_sniffer'');\r\n			$sess->freeze();\r\n			\r\n            // redirect\r\n    		$sf_idcatside_redirect = $mod[''redirect_login''];\r\n    		if ($sf_idcatside_redirect < 1) {\r\n    		  $sf_idcatside_redirect = $idcatside;\r\n    		}\r\n			sf_header_redirect($cfg_client[''contentfile'']."?lang=$lang&idcatside=$sf_idcatside_redirect&".$sess->name.''=''.$sess->id);\r\n		}\r\n	}\r\n\r\n	// Login fails\r\n	if ($auth->auth[''uid''] == ''nobody'') {\r\n		$mod[''throw_error''] = true;\r\n	}\r\n}\r\n\r\nif ($action == ''logout'') {\r\n	$auth->logout();\r\n	$sf_idcatside_redirect = $mod[''redirect_logout''];\r\n\r\n	if ($sf_idcatside_redirect > 0) {\r\n	    sf_header_redirect($cfg_client[''contentfile'']."?lang=$lang&idcatside=$sf_idcatside_redirect");\r\n	} else {\r\n        sf_header_redirect($cfg_client[''contentfile'']."?lang=$lang&idcatside=$idcatside");\r\n    }\r\n}\r\nif ($uid != ''nobody'') {\r\n    $auth->auth[''exp''] = time() + (60 * $auth->lifetime);\r\n}\r\n\r\n//tpl login\r\nif ($auth->auth[''uid''] == ''nobody'') {\r\n    $mod[''tpl_login_error''] = ($mod[''throw_error'']) ? $mod[''tpl_login_error'']:''''; \r\n\r\n	$mod[''usr_input''] = "<input type=\\"text\\" name=\\"username\\" value=\\"".htmlentities($username, ENT_COMPAT, ''UTF-8'')."\\" size=\\"10\\" maxlength=\\"32\\" ".$mod[''attributes_username'']." />\r\n							<input type=\\"hidden\\" name=\\"action\\" value=\\"login\\" />";\r\n    $mod[''pwd_input''] = "<input type=\\"password\\" name=\\"password\\" size=\\"10\\" maxlength=\\"32\\" ".$mod[''attributes_username'']." />";\r\n    \r\n    $mod[''tpl_form''] = $mod[''tpl_login''];\r\n	$mod[''tpl_form''] = str_replace(''{input_name}'', $mod[''usr_input''], $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{input_password}'', $mod[''pwd_input''], $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{input_password}'', $mod[''pwd_input''], $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{errortemplate}'', $mod[''tpl_login_error''], $mod[''tpl_form'']);\r\n	   \r\n//tpl logout\r\n} else {\r\n\r\n    if ($mod[''redirect_if_logged_in''] > 0 && $_REQUEST[''action''] != ''login'' && $_REQUEST[''action''] != ''logout'' &&\r\n    		( ($mod[''redirect_if_logged_in_and backend''] == ''true'' && $sess->name == ''sefrengo'')\r\n    		|| ($sess->name != ''sefrengo'') ) )\r\n    \r\n    {\r\n$addon = '''';\r\nif ($sess->name == ''sefrengo'')\r\n{\r\n  $addon = ''&view=''.$view;\r\n}\r\n        sf_header_redirect($cfg_client[''htmlpath''].$cfg_client[''contentfile'']."?lang=$lang&idcatside=".$mod[''redirect_if_logged_in''].$addon);\r\n    }\r\n    \r\n    $sf_user =& sf_factoryGetObject(''ADMINISTRATION'', ''User''); \r\n    $sf_user->loadByIduser( $auth->auth[''uid''] );\r\n	$mod[''tpl_form''] = $mod[''tpl_logout''];\r\n	$mod[''tpl_form''] = str_replace(''{id}'', $sf_user->getIduser(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{loginname}'', $sf_user->getUsername(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{name}'', $sf_user->getName(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{surname}'', $sf_user->getSurname(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{email}'', $sf_user->getEmail(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{firm}'', $sf_user->getFirm(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{position}'', $sf_user->getPosition(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{salutation}'', $sf_user->getSalutation(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{street}'', $sf_user->getStreet(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{zip}'', $sf_user->getZip(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{location}'', $sf_user->getLocation(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{phone}'', $sf_user->getPhone(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{fax}'', $sf_user->getFax(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{homepage}'', $sf_user->getHomepage(), $mod[''tpl_form'']);\r\n	$mod[''tpl_form''] = str_replace(''{comment}'', $sf_user->getComment(), $mod[''tpl_form'']);\r\n	\r\n    $mod[''timestamp_lastlogin''] = (int) $sf_user->getLastLoginTimestamp();\r\n    if ($mod[''timestamp_lastlogin''] > 0) {\r\n        $mod[''tpl_lastlogin''] = str_replace(''{lastlogin_date}'', date($mod[''dateformat''], $mod[''timestamp_lastlogin'']), $mod[''tpl_lastlogin'']);\r\n        $mod[''tpl_lastlogin''] = str_replace(''{lastlogin_time}'', date($mod[''timeformat''], $mod[''timestamp_lastlogin'']), $mod[''tpl_lastlogin'']);\r\n        $mod[''tpl_form''] = str_replace(''{lastlogin}'', $mod[''tpl_lastlogin''], $mod[''tpl_form'']);\r\n    } else {\r\n        $mod[''tpl_form''] = str_replace(''{lastlogin}'', $mod[''tpl_lastlogin_empty''], $mod[''tpl_form'']);\r\n    }\r\n    \r\n    $mod[''hidden_input''] = "<input type=\\"hidden\\" name=\\"action\\" value=\\"logout\\" />";\r\n	$mod[''tpl_form''] = str_replace(''{form_hiddenvals}'', $mod[''hidden_input''], $mod[''tpl_form'']);\r\n	\r\n    unset($sf_user);\r\n}\r\n\r\n$mod[''tpl_form''] = str_replace(''{form_attributes}'', "action=\\"".$con_side[$idcatside][''link'']."\\" method=\\"post\\" name=\\"login\\"", $mod[''tpl_form'']);\r\necho $mod[''tpl_form''];\r\nunset($mod);\r\n\r\n\r\n}\r\n</CMSPHP>\r\n', '', 0, 1, 1, 1079610875, 1215636782, 'mod:d56b699830e77ba53855679cb1d252da:a75a7742', '', '', '', 0, '', 'Login', '1');
INSERT INTO `cms_mod` VALUES (17, 'Info', '1.0', 'Tools', 'Zeigt allgemeine Informationen für Seite, Ordner und andere Infos an.', '<?php\r\n// Schriftart normal\r\n$mip_form[''0''][''desc''] = $mod_lang[''gen_font''];\r\n$mip_form[''0''][''cat''] = ''app_css'';\r\n$mip_form[''0''][''output_cat''] = ''option'';\r\n$mip_form[''0''][''cms_var''] = ''MOD_VAR[0]'';\r\n$mip_form[''0''][''cms_val''] = $cms_mod[''value''][''0''];\r\n$mip_form[''0''][''cms_val_default''] = ''font2'';\r\n$mip_form[''0''][''flag''] = ''class_only'';\r\n\r\n// Schriftart Infos\r\n$mip_form[''1''][''desc''] = $mod_lang[''gen_font''].'' der Infos'';\r\n$mip_form[''1''][''cat''] = ''app_css'';\r\n$mip_form[''1''][''output_cat''] = ''option'';\r\n$mip_form[''1''][''cms_var''] = ''MOD_VAR[1]'';\r\n$mip_form[''1''][''cms_val''] = $cms_mod[''value''][''1''];\r\n$mip_form[''1''][''cms_val_default''] = ''font1'';\r\n$mip_form[''1''][''flag''] = ''class_only'';\r\n\r\n// Template\r\n$mip_form[''2''][''desc''] = ''Template'';\r\n$mip_form[''2''][''cat''] = ''txtarea'';\r\n$mip_form[''2''][''rows''] = ''6'';\r\n$mip_form[''2''][''type''] = ''long'';\r\n$mip_form[''2''][''cms_var''] = ''MOD_VAR[2]'';\r\n$mip_form[''2''][''cms_val''] = $cms_mod[''value''][''2''];\r\n$mip_form[''2''][''cms_val_default''] = ''letzte Änderung: am {lastmodified_date} um {lastmodified_time} Uhr von {authorname} {authorsurname}'';\r\n\r\nmip_formsp($mip_form[''0'']);\r\nmip_formsp($mip_form[''1'']);\r\nmip_formsp($mip_form[''2'']);\r\necho '' <small><b>Legende:</b><br>{author} ID des Redakteurs, {authorusername} Benutzerkennung des Redakteurs, {authorname} Vorname, {authorsurname} Nachname<br>{created} erstellt am, {created_date} erstellt am (Datum), {created_time} erstellt am (Uhrzeit)<br>{lastmodified} letzte Änderung, {lastmodified_date} Datum, {lastmodified_time} Uhrzeit<br>{lastmodified_projekt} letzte Änderung im Projekt, {lastmodified_projekt_date} Datum, {lastmodified_projekt_time} Uhrzeit<br>{name} Seitenname, {notes} Notizen, {summary} Kurzbeschreibung, {catname} Ordnername<br>{time} aktuelle Uhrzeit, {day} aktueller Tag, {date} aktuelles Datum<br>Falls Statistikplugin vorhanden: {visits} Anzahl Aufrufe der aktuellen Seite, {allvisits} Anzahl Aufrufe aller Seiten des Projekts<br> Php-Code: <CMSPHP> echo "Ich bin phpcode!"; </CMSPHP></small>'';\r\nunset($mip_form);\r\n?>', '<CMSPHP>\r\n<?PHP\r\nif ($cms_mod[''value''][1]) {\r\n               $formatting_front=''<span class="''.$cms_mod[''value''][1].''">'';\r\n               $formatting_end=''</span>'';\r\n} else {\r\n               $formatting_front='''';\r\n               $formatting_end='''';\r\n}\r\n$tpl[''code''] = str_replace(''{author}'', $formatting_front.$con_side[$idcatside][''author''].$formatting_end, $cms_mod[''value''][2]);\r\nif (strpos($tpl[''code''],''{authorusername}'')!==FALSE || strpos($tpl[''code''],''{authorname}'')!==FALSE || strpos($tpl[''code''],''{authorsurname}'')!==FALSE) {\r\n	$db->query(''SELECT username,name,surname FROM ''.$cms_db[''users''].'' WHERE user_id=''.$con_side[$idcatside][''author'']);\r\n	$db->next_record();\r\n	$tpl[''authorusername''] = $db->f(''username'');\r\n	$tpl[''authorname''] = $db->f(''name'');\r\n	$tpl[''authorsurname''] = $db->f(''surname'');\r\n}\r\n$tpl[''code''] = str_replace(''{authorusername}'', $formatting_front.$tpl[''authorusername''].$formatting_end, $tpl[''code'']);\r\n$tpl[''code''] = str_replace(''{authorname}'', $formatting_front.$tpl[''authorname''].$formatting_end, $tpl[''code'']);\r\n$tpl[''code''] = str_replace(''{authorsurname}'', $formatting_front.$tpl[''authorsurname''].$formatting_end, $tpl[''code'']);\r\n$tpl[''code''] = str_replace(''{created}'', $formatting_front.date($cfg_cms[''FormatDate''].'' ''.$cfg_cms[''FormatTime''], $con_side[$idcatside][''created'']).$formatting_end, $tpl[''code'']);\r\n$tpl[''code''] = str_replace(''{created_date}'', $formatting_front.date($cfg_cms[''FormatDate''], $con_side[$idcatside][''created'']).$formatting_end, $tpl[''co