r\n        if (! in_array($how,array("AND","OR","QUOT"))) {\r\n                $how = "AND";\r\n        }\r\n        //$sort ueberpruefen\r\n        if (! in_array($sort,array("C.idcatside","B.title","B.summary","B.meta_description","B.meta_keywords"))) {\r\n                $sort = "C.idcatside";\r\n        }\r\n        $mod[''sql_fields''] = implode(",",array_diff(array($w_70,$w_71,$w_72,$w_73,$w_74),array("")));\r\n        switch($what){\r\n                case "none": $mod[''get_fields'']    = "B.title,C.idcatside"; break;\r\n                case "summary": $mod[''get_fields''] = "B.title,C.idcatside,B.summary"; break;\r\n                case "meta_description": $mod[''get_fields''] = "B.title,C.idcatside,B.meta_description"; break;\r\n                case "body": $mod[''get_fields'']    = "B.title,C.idcatside,A.value"; break;\r\n        }\r\n        if("MOD_VALUE[11]" == "0"){\r\n                $mod[''res_limit''] = "";\r\n                $mod[''res_start''] = "";\r\n        } else {\r\n                $mod[''res_limit''] = "MOD_VALUE[11]";\r\n                if($page < 1 || !$page)\r\n                        {\r\n                                $page = "1";\r\n                                $mod[''res_start''] = 0;\r\n                        } else {\r\n                                if (! is_numeric($page))\r\n                                {\r\n                                        $mod[''res_start''] = 0;\r\n                                } else {\r\n                                        $mod[''res_start''] = ($page-1) * "MOD_VALUE[11]";\r\n                                }\r\n                        }\r\n        }\r\n\r\n                if ($mod[''test''] == "true") {echo "<hr />Diverses: [22]:MOD_VALUE[22] [23]:MOD_VALUE[23] [20]:MOD_VALUE[20]<br />";}\r\n\r\n        \r\n// Suche auf Seiten beschraenken fals erwuenscht\r\n        $mod[''sql_sidecat''] = "";\r\n        if(''MOD_VALUE[22]'' == ''none'') {\r\n                // alle Seiten/Kategorien