  (B.online & 0x03) = 0x01 OR (  (B.online & 0x02) = 0x02 AND ( UNIX_TIMESTAMP(NOW()) BETWEEN B.start AND B.end))) ";\r\n\r\n                // Abfrage Recht: Darf geschuetzte Seiten sehen\r\n                $mod[''sql_online''] .=  ( $perm->have_perm(2, ''area_frontend'', 0) ) ?  '''': '' AND (B.online & 0x04) = 0x00 '';\r\n        }\r\n\r\n        if(("MOD_VALUE[25]" != "") AND (substr_count("MOD_VALUE[25]","true")==0)) {\r\n                $mod[''sql_content''] = " AND (A.idtype IN (MOD_VALUE[25]))";\r\n        } else {\r\n                $mod[''sql_content''] = " AND (A.idtype IN (1,2,3,7,9,11,13))";\r\n        }\r\n\r\n        if ($mod[''test''] == "true") {echo "<hr />Kontrolle:".implode('','',$_POST)." --> GET 70:".$w_70." 71:".$w_71." 72:".$w_72." 73:".$w_73." 74:".$w_74."<br />lang: ".$lang." sql: ".$mod[''sql_lang''];}\r\n        if ($mod[''test''] == "true") {echo "<br />Suche: $suche<br />Suche in: ".$mod[''sql_fields'']."<br />Hole: ".$mod[''get_fields'']."<br />Verbinde: $how<br />Sortiere: $sort<br />Page: ".$mod[''res_start''].",".$mod[''res_limit''];}\r\n        if ($mod[''test''] == "true") {echo "<br />sql_where:".$mod[''sql_lang''].$mod[''sql_online''].$mod[''sql_content''].$mod[''sql_sidecat'']."<br /><hr />";}\r\n        $result = search(array(\r\n                        "querystring" => $suche,\r\n                        "sql_tables"  => $cms_db[''content'']." A, ".$cms_db[''side_lang'']." B, ".$cms_db[''cat_side'']." C,".$cms_db[''side'']." D",\r\n                        "sql_fields"  => $mod[''sql_fields''],\r\n                        "get_fields"  => $mod[''get_fields''],\r\n                        "sql_where"   => "A.idsidelang = B.idsidelang AND B.idside = C.idside AND B.idside = D.idside AND D.idclient = ".$client.$mod[''sql_lang''].$mod[''sql_online''].$mod[''sql_content''].$mod[''sql_sidecat''],\r\n                        "sql_group"   => "C.idcatside",\r\n                        "result_limit"=> $mod[''res_limit''],\r\n                        "result_start"=> $mod[''res_start''],\r\n                        "sort_index"  => $sort,\r\n                        "default_con" => $how\r\n                        )\r\n        );\r\n        // Wir brauchen die Seiten, die wir ausschliessen wollen\r\n        if ($mod[''test''] == "true") {echo "num_rows: ".$result[''num_rows'']."<hr />";}\r\n\r\n        $mod[''nr''] = $mod[''res_start'']+1;\r\n        if($result[''num_rows''] > 0){\r\n                foreach($result[''result''] as $mod[''res'']){\r\n                        if ($mod[''test''] == "true") {echo "Kontrolle Result<br />IDcatside: ".$mod[''res''][''idcatside'']."<br />Title: ".$mod[''res''][''title'']."<br>Nr: ".$mod[''nr'']."<hr />";}\r\n                        $mod[''repl_string''] = "\\n<a href=''".$con_side[$mod[''res''][''idcatside'']][''link'']."'' class=''MOD_VALUE[0]''>".$mod[''res''][''title'']."</a>\\n";\r\n                        $mod[''tpl_res''] = str_replace("{link}",$mod[''repl_string''],"MOD_VALUE[16]");\r\n                        if($what == "none"){\r\n                                $mod[''repl_string''] = "";\r\n                        } elseif($what == "summary"){\r\n                                $mod[''repl_string''] = "<p class=''MOD_VALUE[1]''>".$mod[''res''][''summary'']."</p>";\r\n                        } elseif($what == "meta_description"){\r\n                                $mod[''repl_string''] = "<p class=''MOD_VALUE[1]''>".$mod[''res''][''meta_description'']."</p>";\r\n                        } else {\r\n                                $mod[''repl_string''] = "<p class=''MOD_VALUE[1]''>";\r\n                                $mod[''res''][''value''] = strip_tags($mod[''res''][''value'']);\r\n                                $mod[''searchvalue''] = explode (" ", $suche);\r\n                                $mod[''searchstart''] = strpos ($mod[''res''][''value''],$mod[''searchvalue''][0])-40;\r\n                                if ($mod[''searchstart''] < 0) {\r\n                                        $mod[''searchstart'']=0;\r\n                                } else {\r\n                                        $mod[''repl_string''] .= "...";\r\n                                        $mod[''searchstart''] = strpos ($mod[''res''][''value'']," ",$mod[''searchstart'']);\r\n                                }\r\n\r\n                                if ($mod[''test''] == "true") {echo "Wort:''".$mod[''searchvalue''][0]."'' Start:".$mod[''searchstart'']."<hr />";}\r\n                                $mod[''repl_string''] .= substr($mod[''res''][''value''],$mod[''searchstart''],300)."...</p>";\r\n                        }\r\n                        $mod[''tpl_res''] = str_replace("{descr}",$mod[''repl_string''],$mod[''tpl_res'']);\r\n                        $mod[''tpl_res''] = str_replace("{nr}",$mod[''nr''],$mod[''tpl_res'']);\r\n                        $mod[''tpl_output''] .= $mod[''tpl_res''];\r\n                        $mod[''nr'']=$mod[''nr'']+1;\r\n                }\r\n        } else {\r\n                $mod[''tpl_res''] = "<p class=''MOD_VALUE[4]''>MOD_VALUE[19]</p>";\r\n                $mod[''tpl_output''] .= $mod[''tpl_res''];\r\n        }\r\n\r\n        $mod[''tpl_res_pre''] = str_replace("{count}",$result[''num_rows''],"MOD_VALUE[15]");\r\n\r\n        if($result[''num_rows''] > 0 && $mod[''res_limit''] != "" ) {\r\n                $mod[''anz''] = $result[''num_rows''];\r\n                $mod[''query''] = "&amp;w_70=$w_70&amp;w_71=$w_71&amp;w_72=$w_72&amp;w_73=$w_73&amp;w_74=$w_74&amp;what=$what&amp;how=$how&amp;modus=$modus&amp;sort=$sort&amp;suche=";\r\n                $mod[''query''] .= rawurlencode($suche);\r\n\r\n                // gesamtanzahl der seiten herausfinden\r\n                if("MOD_VALUE[11]" == "0"){\r\n                        $mod["anz"] = 1;\r\n                } else {\r\n                        $mod["anz"] = ceil($result[''num_rows''] / "MOD_VALUE[11]");\r\n                }\r\n\r\n                // wenn