, Höhe des Bilds Y, Breite des Watermarks, Höhe des Bildes, Transparenz)\r\n			imagecopymerge($cms_image->imageHandle, $cms_watermark->imageHandle, $x, $y, 0, 0, $watermark_width, $watermark_height, $transition);\r\n			$cms_image->save($new_image);\r\n			$cms_image->free();\r\n			unset($cms_image);\r\n		}\r\n	}\r\n\r\n\r\n//Funktion zum auslesen der Thumbgrößen\r\n	if (!function_exists(''get_thumb_size'')) {\r\n		function get_thumb_size($xy, $thumb) {\r\n			global $cfg_cms, $cfg_client;\r\n			require_once ''Image/Transform.php'';\r\n			$cms_image = Image_Transform::factory($cfg_cms[''image_mode'']);\r\n			$cms_image->load($thumb);\r\n\r\n			//if ($x != $cms_image->img_x || $y != $cms_image->img_y) $cms_image->resize($x, $y);\r\n			//$watermark = imagecreatefromstring(base64_decode($watermarkfile));\r\n                         $width = imagesx($cms_image->imageHandle);\r\n                         $height = imagesy($cms_image->imageHandle);\r\n			 $cms_image->free();\r\n			 unset($cms_image);\r\n\r\n			 if ($xy == ''x'') {\r\n			 return $width;\r\n			 }\r\n			 elseif ($xy == ''y'') {\r\n			 return $height;\r\n			 }\r\n\r\n		}\r\n	}\r\n\r\n\r\n\r\n//Funktion zum umwandeln von Bilder IDs in Pfade\r\n    if(! function_exists(get_path)){\r\n			    function get_path($id)\r\n			    {\r\n			     global $cfg_cms, $cfg_client, $cms_db, $db, $cms_mod;\r\n\r\n			     $sql = "SELECT A.*, B.dirname\r\n			     FROM\r\n			     ".$cms_db[''upl''] ." as A, ". $cms_db[''directory''] ." as B\r\n			     WHERE\r\n			     idupl=''".$id."''\r\n			     AND\r\n			     A.iddirectory = B.iddirectory";\r\n\r\n			     $db->query($sql);\r\n			     $db->next_record();\r\n			     $filepath = $cfg_client["upl_path"].$db->f(''dirname'').$db->f(''filename'');\r\n			     return $filepath;\r\n			    }\r\n			 }\r\n\r\n\r\n\r\n\r\n\r\n###########################################################################################################################\r\n#Hier ein paar Variablen, die wir überall brauchen\r\n###########################################################################################################################\r\n\r\n//Variable für den Link zur aktuellen Seite mit Session ID\r\n$mod[''idcatlink''] =  $con_side[$idcatside][''link''];\r\n\r\n//Auslesen ob im Frontend-Editiert werden darf\r\n$mod[''frontedit''] = "MOD_VALUE[51]";\r\n\r\n//Auslesen ob mit Detailansicht gestartet wird\r\n$mod[''detailid_start''] = "MOD_VALUE[60]";\r\n\r\n//Auslesen ob Thumbs in der Detailansicht verwendet werden\r\n$mod[''detailid_thumb''] = "MOD_VALUE[61]";\r\n\r\n//Ordner beim Editieren im Frontend (inkl. Container für mehrere Galerien pro Seite\r\n$mod[''change_dir_string''] = "change_dir_".$cms_mod[''container''][''id''];\r\n$mod[''change_dir''] = ${$mod[''change_dir_string'']};\r\n\r\n//Anzahl der Bilder pro Seite\r\n$mod["number_of_pics_per_page"] = MOD_VALUE[6];\r\n\r\n//Anzahl der Bilder pro Zeile\r\n$mod["break_after_number_of_pics"] = MOD_VALUE[7];\r\n\r\n//Thumbtext im Projekt\r\n$mod["filename_suffix"] = $cfg_client["thumbext"];\r\n\r\n//Auslesen welche Galerieseite angezeigt werden soll\r\nif (isset($galerie))\r\n{\r\n     $mod["galerie"] = array();\r\n     $mod["galerie"] = explode("_",$galerie);\r\n     for ($i = 0; $i < count($mod["galerie"])/2; $i++)\r\n     { if ($cms_mod[''container''][''id''] == $mod["galerie"][$i*2])\r\n       { $id = $mod["galerie"][$i*2+1];\r\n       }\r\n       else\r\n       { $mod[''query''] .= "_" . $mod["galerie"][$i*2] . "_" . $mod["galerie"][$i*2+1];\r\n       }\r\n     }\r\n}\r\n\r\n//soll eine bestimmte Galerieseite angezeigt werden?\r\nif (!isset($id))\r\n{ if ("MOD_VALUE[5]" == "1")\r\n  { $id = 1;\r\n  }\r\n  else\r\n  { $id = "MOD_VALUE[5]";\r\n  }\r\n}\r\n\r\n//Falls Thumbs in der Detailansicht gezeigt werden umblättern bei Detailbildern\r\nif ($mod[''detailid_thumb''] AND !$galerie AND $detailid) {\r\n$id = ceil($detailid / $mod["number_of_pics_per_page"]);\r\n}\r\n\r\n// Automatisch die Dateilseiten zeigen und dann beim umblättern die neueste Detailid zeigen\r\nif(!isset($detailid) AND $sess->name != ''cms'' AND $mod[''detailid_start'']  AND !$galerie) {\r\n  $detailid = 1;\r\n} elseif (!isset($detailid) AND $sess->name != ''cms'' AND $mod[''detailid_start'']  AND $galerie) {\r\n $detailid = (($id-1) * $mod["number_of_pics_per_page"])+1 ;\r\n}\r\n\r\n\r\n##########################################################################################################################\r\n  // FRONTEND BEARBEITUNG (wird in der Detailansicht ausgeschlossen! Habe das für nicht sinnvoll gehalten. Läßt sich aber leicht wieder aktivieren.)\r\n##########################################################################################################################\r\n\r\n//Frontend-editieren\r\nif ($mod[''frontedit''] == true)\r\n\r\n    {\r\n\r\n    //bjoerns funktion\r\n    if(! function_exists(get_val)){\r\n    function get_val($value_name)\r\n    {\r\n     global $idcatside, $cms_db, $db, $con_side, $cms_mod;\r\n\r\n     $sql = "SELECT\r\n        *\r\n       FROM\r\n        ".$cms_db[''content_external''] ."\r\n       WHERE\r\n        idsidelang=''".$con_side[$idcatside][''idsidelang'']."''\r\n        AND container=''".$cms_mod[''container''][''id'']."''\r\n        AND idtype=''$value_name''";\r\n     $db->query($sql);\r\n     $db->next_record();\r\n\r\n     return $db->f(''value'');\r\n    }\r\n    }\r\n\r\n    if(! function_exists(set_val)){\r\n    function set_val($value_name, $value)\r\n    {\r\n     global $idcatside, $cms_db, $db, $con_side, $cms_mod;\r\n     //Schauen, ob es den Wert schon gibt\r\n     $sql = "SELECT\r\n        *\r\n       FROM\r\n        ".$cms_db[''content_external''] ."\r\n       WHERE\r\n        idsidelang=''".$con_side[$idcatside][''idsidelang'']."''\r\n        AND container=''".$cms_mod[''container''][''id'']."''\r\n        AND idtype=''$value_name''";\r\n     $db->query($sql);\r\n\r\n     //Es gibt den Wert schon -> wert aktuallisieren\r\n     if ($db->next_record()){\r\n      $sql = "UPDATE\r\n         ". $cms_db[''content_external''] ."\r\n        SET\r\n         value=''$value''\r\n        WHERE\r\n         idsidelang=''".$con_side[$idcatside][''idsidelang'']."''\r\n         AND container=''".$cms_mod[''container''][''id'']."''\r\n         AND idtype=''$value_name''";\r\n     }\r\n     //Es gibt den Wert noch nicht, neu in tabelle einfügen\r\n     else{\r\n      $sql = "INSERT INTO\r\n         ". $cms_db[''content_external''] ."\r\n         (idsidelang, container, idtype, value)\r\n        VALUES\r\n         (''".$con_side[$idcatside][''idsidelang'']."'',\r\n          ''".$cms_mod[''container''][''id'']."'',\r\n          ''$value_name'', ''$value'')";\r\n     }\r\n\r\n     $db->query($sql);\r\n\r\n    //Letzte Änderung Datum ändern!\r\n    $sql = "UPDATE ". $cms_db[''side_lang'']. " SET lastmodified=''".time()."'' WHERE idsidelang=''".$con_side[$idcatside][''idsidelang'']."'' ";\r\n                    $db->query($sql);\r\n\r\n     //Cache löschen\r\n     //Seite fürs frontend neu generieren, da sich ein Wert geändert hat\r\n     change_code_status($idcatside, 1, ''idcatside'');\r\n\r\n    }\r\n    }\r\n\r\n    //Editierwechsel beachten\r\n                            if  ($mod[''change_dir'']) {\r\n                                     //Formular ist abgeschickt worden\r\n                                    //Neuen Wert in die DB einfügen\r\n                                    set_val(''galerie_folder'', $mod[''change_dir'']);\r\n                                            }\r\n\r\n    //Wert aus der DB holen\r\n    $mod[''galerie_folder''] = get_val(''galerie_folder'');\r\n\r\n\r\n    //Bearbeitenbutton\r\n    $aktverz = $mod[''galerie_folder''];\r\n    if ($cms_side[''edit'']) {\r\n\r\n    //Startverzeichnis auslesen\r\n    $mod[''startdir''] = "MOD_VALUE[52]";\r\n\r\n    if ($mod[''startdir'']) {\r\n\r\n    //Startverzeichnis Namen auslesen\r\n    $sql = "SELECT A.dirname\r\n       FROM\r\n        ".$cms_db[''directory''] ." as A\r\n       WHERE\r\n        A.iddirectory=''".$mod[''startdir'']."''\r\n    ORDER BY dirname";\r\n    $db -> query($sql);\r\n    $db->next_record();\r\n\r\n    $mod[''startdirname''] =  $db->f(''dirname'');\r\n\r\n     }\r\n\r\n\r\n    if ($mod[''startdirname'']) {\r\n\r\n    //Dateimanager Verzeichnisse auslesen mit Startverzeichnis\r\n    $sql = "SELECT A.iddirectory, A.dirname\r\n       FROM\r\n        ".$cms_db[''directory''] ." as A\r\n       WHERE\r\n        A.idclient=''".$client."''\r\n        AND A.status & 0x05 = 0x00\r\n        AND A.dirname like ''".$mod[''startdirname'']."%''\r\n\r\n    ORDER BY dirname";\r\n    $result = mysql_query($sql);\r\n\r\n    }else{\r\n\r\n    //Dateimanager Verzeichnisse auslesen\r\n    $sql = "SELECT A.iddirectory, A.dirname\r\n       FROM\r\n        ".$cms_db[''directory''] ." as A\r\n       WHERE\r\n        A.idclient=''".$client."''\r\n        AND A.status & 0x05 = 0x00\r\n\r\n    ORDER BY dirname";\r\n    $result = mysql_query($sql);\r\n\r\n    }\r\n\r\n\r\n\r\n    //Aktion auf aktuelle Seite legen\r\n    $action = $con_side[$idcatside][''link''];\r\n    //Bearbeitungsformular ausgeben\r\n    echo "<div align=\\"center\\"><form method=\\"POST\\" action=\\"$action\\"><select name=\\"change_dir_".$cms_mod[''container''][''id'']."\\" size=\\"1\\">";\r\n\r\n    while ($row = mysql_fetch_array($result, MYSQL_ASSOC))\r\n    {\r\n\r\n    if($perm->have_perm(0x01, "folder", $row[''iddirectory'']))\r\n    {\r\n    if ($mod[''galerie_folder''] == $row[''iddirectory''])\r\n    { echo "<option  selected value=\\""; }\r\n    else { echo "<option value=\\"";}\r\n      echo $row[''iddirectory''];\r\n      echo "\\">";\r\n      echo $row[''dirname''];\r\n      echo "</option>";\r\n    }}\r\n    echo"</select>";\r\n    echo"<input type=\\"submit\\" value=\\"Submit\\">";\r\n\r\n		//Kästchen für Watermark neu Berechnung einfügen wenn Watermarkpfad vorhanden\r\n		if (($mod[''watermark_thumb_id'']) OR ($mod[''watermark_thumb_id''])) {\r\n		    echo"<input type=\\"checkbox\\" name=\\"resample_watermarks\\"> Wasserzeichen neu berechnen?";\r\n		   }\r\n\r\necho"</form></div>";\r\n\r\n\r\n    unset($cms_side[''edit'']);\r\n    }\r\n\r\n\r\n    }\r\n    //END-Frontend-Editieren\r\n    else\r\n    {\r\n    //Wenn der Ordner in der Konfiguration eingestellt wird\r\n    $mod[''galerie_folder''] = "MOD_VALUE[4]";\r\n}\r\n\r\n\r\n##########################################################################################################################\r\n  // Daten ermitteln\r\n##########################################################################################################################\r\n//Ist das Verzeichnis in der Datenbank? False bedeutet ja! Weiter unten in der if schleife wird das nochmal möglichweise nochmal überschrieben!\r\n//Das ist wichtig für das Bearbeiten im Frontend. When false wird die Galerie angezeigt, wenn true bedeutet das, dass der Wert in der DB noch nicht\r\n//gesetzt ist, was zu einem SQL-Fehler führen würde, der so abgefangen wird!\r\n$mod[''no_dir''] = false;\r\n\r\n\r\n//diese if-schleife fängt den SQL-Fehler ab wenn im FE noch kein Verzeicnis festgelegt wurde\r\nif ( ($mod[''galerie_folder''] ==false) AND ($mod[''frontedit''] == true))\r\n	{\r\n	$mod[''error_no_dir''] = "<div align=\\"center\\" class=\\"{font1}\\">Diese Galerie ist noch nicht eingerichtet worden.</div>";\r\n	$mod[''error_no_dir''] = str_replace("{font1}", "MOD_VALUE[1]", $mod[''error_no_dir'']);\r\n	echo $mod[''error_no_dir''];\r\n	$mod[''no_dir''] = true;\r\n	}\r\nelse\r\n	{\r\n    //Mit dieser if-Schleife wird für Unterordner gesorgt und für "Alle Ordner"\r\n    if ( ($mod[''galerie_folder''] ==false) AND ($mod[''frontedit''] == false))\r\n        {\r\n        $sql = "SELECT A.*, B.dirname\r\n                            FROM " . $cms_db[''upl''] . " as A, ". $cms_db[''directory''] ." as B\r\n\r\n                            WHERE\r\n                                    A.idclient = ''$client''\r\n                            AND\r\n                                    A.iddirectory = B.iddirectory\r\n                            AND\r\n                                    idfiletype = 3\r\n                            ORDER BY\r\n                                    A." . "MOD_VALUE[15]" . " " . "MOD_VALUE[16]";\r\n        }\r\n\r\n    else\r\n        {\r\n        	//Unterordner berücksichtigen?\r\n            if ("MOD_VALUE[26]" == true)\r\n                {\r\n\r\n				//Name des eingestellten Verzeichnisses auslesen\r\n    			$sqlsub = "SELECT A.dirname\r\n       			FROM\r\n        		".$cms_db[''directory''] ." as A\r\n       			WHERE\r\n        		A.iddirectory=''".$mod[''galerie_folder'']."''\r\n    			ORDER BY dirname";\r\n    			$db -> query($sqlsub);\r\n    			$db->next_record();\r\n			    $mod[''name_of_galerie_folder''] =  $db->f(''dirname'');\r\n\r\n				//IDs der Unterordner auslesen\r\n			    $sqlsub = "SELECT A.iddirectory\r\n       			FROM\r\n        		".$cms_db[''directory''] ." as A\r\n       			WHERE\r\n        		A.idclient=''".$client."''\r\n			    AND A.dirname like ''".$mod[''name_of_galerie_folder'']."%''";\r\n				$mod[''sub_dirs''] = mysql_query($sqlsub);\r\n\r\n  				//array mit den ids füllen\r\n  				$mod[''sub_dirs_ids''] = array();\r\n  				while ($row = mysql_fetch_array($mod[''sub_dirs''], MYSQL_ASSOC))\r\n   				 {\r\n   				  array_push($mod[''sub_dirs_ids''], "OR B.parentid = ".$row[''iddirectory'']."");\r\n				 }\r\n   				 $mod[''sub_dirs_ids''] = implode(" ", $mod[''sub_dirs_ids'']);\r\n\r\n				//SQL Where Abfrage für Unterverzeichnisse füllen\r\n                $mod[''where''] = "(A.iddirectory = ". $mod[''galerie_folder''] ." ".$mod[''sub_dirs_ids''].")";\r\n                }\r\n                                else\r\n                {\r\n                $mod[''where''] = "A.iddirectory = ". $mod[''galerie_folder''] ." ";\r\n                }\r\n\r\n        $sql = "SELECT A.*, B.dirname\r\n            FROM " . $cms_db[''upl''] . " as A, ". $cms_db[''directory''] ." as B\r\n\r\n            WHERE\r\n                " . $mod[''where''] . "\r\n            AND\r\n                A.idclient = ''$client''\r\n            AND\r\n                A.iddirectory = B.iddirectory\r\n            ORDER BY\r\n                A." . "MOD_VALUE[15]" . " " . "MOD_VALUE[16]";\r\n        }\r\n\r\n             $db->query($sql);\r\n             $i = 0;\r\n\r\n             //Anfang von while\r\n             while ($db->next_record() )\r\n             { $mod["file"][$i]["title"] = $db->f(''titel'');\r\n               $mod["file"][$i]["filename"] = $db->f(''filename'');\r\n               $mod["file"][$i]["idupl"] = $db->f(''idupl'');\r\n               $mod["file"][$i]["description"] = $db->f(''description'');\r\n               $mod["file"][$i]["dirname"] = $db->f(''dirname'');\r\n               $mod["file"][$i]["filesize"] = $db->f(''filesize'');\r\n               $mod["file"][$i]["width"] = $db->f(''pictwidth'');\r\n               $mod["file"][$i]["height"] = $db->f(''pictheight'');\r\n               $mod["file"][$i]["thumbwidth"] = $db->f(''pictthumbwidth'');\r\n               $mod["file"][$i]["thumbheight"] = $db->f(''pictthumbheight'');\r\n            ### BEGIN filter  für <order>\r\n                   if (strstr($mod["file"][$i]["description"],"<order>"))\r\n                   { $mod[''order_firstpos''] = strpos($mod["file"][$i]["description"],"<order>")+7;\r\n                     $mod[''order_lastpos'']  = strpos($mod["file"][$i]["description"],"</order>");\r\n                     $mod["file"][$i][''order''] = substr($mod["file"][$i]["description"],$mod[''order_firstpos''],$mod[''order_lastpos'']-$mod[''order_firstpos'']);\r\n                     $mod[''order_tag''] = "<order>". $mod["file"][$i][''order''] ."</order>";\r\n                     $mod["file"][$i]["description"] = str_replace($mod[''order_tag''], "" ,$mod["file"][$i]["description"]);\r\n                   }\r\n                   else\r\n                   { $mod["file"][$i][''order''] = "ZZZ";   // wenn keine Reihenfolge definiert, dann hinten dran\r\n                   }\r\n            ### END filter  für <order>\r\n\r\n            ### BEGIN  maximgsize\r\n            if ("MOD_VALUE[17]" != "")\r\n            {\r\n                                    if ( ($mod["file"][$i]["width"] > "MOD_VALUE[17]") OR ( $mod["file"][$i]["height"] > "MOD_VALUE[17]") )\r\n                                    { if ($mod["file"][$i]["width"] > $mod["file"][$i]["height"])\r\n                                      {    $mod["file"][$i]["h_show"] = round($mod["file"][$i]["height"] * "MOD_VALUE[17]" / $mod["file"][$i]["width"]);\r\n                                           $mod["file"][$i]["w_show"] = "MOD_VALUE[17]";\r\n                                      }\r\n                                      ELSE\r\n                                      {    $mod["file"][$i]["w_show"] = round($mod["file"][$i]["width"] * "MOD_VALUE[17]" / $mod["file"][$i]["height"]);\r\n                                           $mod["file"][$i]["h_show"] = "MOD_VALUE[17]";\r\n                                      }\r\n                                    }\r\n                                    ELSE\r\n                                    { $mod["file"][$i]["w_show"] = $mod["file"][$i]["width"];\r\n                                      $mod["file"][$i]["h_show"] = $mod["file"][$i]["height"];\r\n                                    }\r\n               $mod["file"][$i]["maximgsize"] = "width=\\"" . $mod["file"][$i]["w_show"] . "\\" height=\\"" . $mod["file"][$i]["h_show"] . "\\"";\r\n            }\r\n            ### END maximgsize\r\n            ### BEGIN  maximgsize2\r\n            if ("MOD_VALUE[21]" != "")\r\n            {\r\n                                    if ( ($mod["file"][$i]["width"] > "MOD_VALUE[21]") OR ( $mod["file"][$i]["height"] > "MOD_VALUE[21]") )\r\n                                    { if ($mod["file"][$i]["width"] > $mod["file"][$i]["height"])\r\n                                      {    $mod["file"][$i]["h_show"] = round($mod["file"][$i]["height"] * "MOD_VALUE[21]" / $mod["file"][$i]["width"]);\r\n                                           $mod["file"][$i]["w_show"] = "MOD_VALUE[21]";\r\n                                      }\r\n                                      ELSE\r\n                                      {    $mod["file"][$i]["w_show"] = round($mod["file"][$i]["width"] * "MOD_VALUE[21]" / $mod["file"][$i]["height"]);\r\n                                           $mod["file"][$i]["h_show"] = "MOD_VALUE[21]";\r\n                                      }\r\n                                    }\r\n                                    ELSE\r\n                                    { $mod["file"][$i]["w_show"] = $mod["file"][$i]["width"];\r\n                                      $mod["file"][$i]["h_show"] = $mod["file"][$i]["height"];\r\n                                    }\r\n               $mod["file"][$i]["maximgsize2"] = "width=\\"" . $mod["file"][$i]["w_show"] . "\\" height=\\"" . $mod["file"][$i]["h_show"] . "\\"";\r\n            }\r\n            ### END maximgsize2\r\n\r\n               $i++;\r\n\r\n            // Ende von while\r\n             }\r\n\r\n	//Ende der if-else-schleife für das Abfangen des SQL-Fehlers bei nicht vergebenem Verzeichnis\r\n	}\r\n\r\n\r\n    ### BEGIN individual order\r\n    if (MOD_VALUE[15] == "description")\r\n    {\r\n      function cmp ($a, $b)\r\n      { return strcmp($a["order"], $b["order"]);\r\n      }\r\n      function rcmp ($a, $b)\r\n      { return strcmp($b["order"], $a["order"]);\r\n      }\r\n\r\n      if (MOD_VALUE[16] == "ASC")\r\n      { usort($mod["file"], "cmp");}\r\n      else\r\n      { usort($mod["file"], "rcmp");}\r\n    }\r\n    ###  // END individual order\r\n\r\n\r\n\r\n    $i = ($id - 1) * $mod["number_of_pics_per_page"];\r\n    $mod["last_pic"] = $id * $mod["number_of_pics_per_page"];\r\n    if ($mod["last_pic"] > count($mod["file"]))\r\n   		 { $mod["last_pic"] = count($mod["file"]);\r\n    	 }\r\n\r\n##########################################################################################################################\r\n  // Galerie-Walker:\r\n##########################################################################################################################\r\n\r\nif ( !function_exists(''galeriecreatePageLinks'') ) {\r\nfunction galeriecreatePageLinks($isFloating, $separator, $showNavLinks) {\r\n        global $mod, $cfg_client, $idcatside, $id, $cms_mod;\r\n        if ($isFloating > 0) {\r\n                $floatdiff = ceil($isFloating / 2);\r\n\r\n                $start = $id - $floatdiff;\r\n                if ($start > $mod["anz"]) $start = $mod["anz"] - $floatdiff;\r\n                if ($start < 1) $start = 1;\r\n\r\n                $stop = $id + $floatdiff;\r\n                if ($stop > $mod["anz"]) $stop = $mod["anz"];\r\n        } else {\r\n                $start = 1;\r\n                $stop = $mod["anz"];\r\n        }\r\n\r\n        if ($id > 1 && $showNavLinks == ''true'') {\r\n                $previd = $id - 1;\r\n                $returnvalue .= "<a href=\\"".$mod[''idcatlink'']."&amp;galerie=".$cms_mod[''container''][''id'']."_".$previd.$mod[''query'']."\\">MOD_VALUE[13]</a>";\r\n        }\r\n\r\n        for ( $mod["pagecounter"] = $start; $mod["pagecounter"] <= $stop; $mod["pagecounter"]++ )\r\n        {\r\n                if ($mod["pagecounter"] > $start) $returnvalue .= $separator;\r\n                if ($mod["pagecounter"] == $id) {\r\n                       if ("MOD_VALUE[18]" == false){\r\n                        $returnvalue .= "<span class=\\"MOD_VALUE[3]\\">[".$mod["pagecounter"]."]</span>";\r\n                        } else {\r\n                        $returnvalue .= "<a href=\\"".$mod[''idcatlink'']."&amp;galerie=".$cms_mod[''container''][''id'']."_".$mod["pagecounter"].$mod[''query'']."\\"><span class=\\"MOD_VALUE[3]\\">[".$mod["pagecounter"]."]</span></a>";\r\n                        }\r\n                } else {\r\n                        $returnvalue .= "<a href=\\"".$mod[''idcatlink'']."&amp;galerie=".$cms_mod[''container''][''id'']."_".$mod["pagecounter"].$mod[''query'']."\\"><span class=\\"MOD_VALUE[2]\\">".$mod["pagecounter"]."</span></a>";\r\n                }\r\n        }\r\n\r\n        if ($id < $mod["anz"] && $showNavLinks == ''true'') {\r\n                $nextid = $id + 1;\r\n                $returnvalue .= "<a href=\\"".$mod[''idcatlink'']."&amp;galerie=".$cms_mod[''container''][''id'']."_".$nextid.$mod[''query'']."\\">MOD_VALUE[14]</a>";\r\n        }\r\n        return $returnvalue;\r\n}  // end function\r\n\r\n} // end if exist\r\n\r\n// gesamtanzahl der seiten herausfinden\r\n$mod["anz"] = ceil(count($mod["file"]) / $mod["number_of_pics_per_page"]);\r\n\r\n// wenn mod_value_12 leer ist, default-separator verwenden.\r\nif ("MOD_VALUE[12]" == "")\r\n{\r\n        $separator = "&nbsp;&nbsp;|&nbsp;&nbsp;";\r\n} else {\r\n        $separator = "MOD_VALUE[12]";\r\n}\r\n\r\n// vor/zurück-links anzeigen?\r\nif ($mod["anz"]> 1)$mod["walker"] .= galeriecreatePageLinks("MOD_VALUE[11]", $separator, "MOD_VALUE[10]");\r\n\r\n##########################################################################################################################\r\n  // Ausgabe\r\n##########################################################################################################################\r\nfor($i;$i<$mod["last_pic"];$i++)\r\n{\r\n  //\r\n\r\n\r\n  $mod[''temp''] = "MOD_VALUE[9]";\r\n\r\n  $mod["file"][$i]["filename"] = trim($mod["file"][$i]["filename"]);\r\n\r\n  $mod["file_ext"] = substr (strrchr ($mod["file"][$i]["filename"], "."), 1);\r\n  $mod["filename"] = substr ($mod["file"][$i]["filename"], 0, -(strlen($mod["file_ext"]))-1 );\r\n\r\n\r\n\r\n\r\n\r\n\r\n//Wasserzeichen\r\n\r\n\r\n//Wasserzeichenposition errechnen\r\n\r\n//links oben\r\nif ($mod[''watermark_image_location''] == ''lo'') {\r\n\r\n}\r\n//mitte obe